import React, { useState, useEffect } from "react";
import axios from "axios";
import html2pdf from "html2pdf.js";
import { useParams, useNavigate } from "react-router-dom";

const ResultsPage = () => {
  const { weekId } = useParams();
  const navigate = useNavigate();
  const [results, setResults] = useState([]);

  useEffect(() => {
    const fetchResults = async () => {
      try {
        const res = await axios.get(`http://localhost:4000/api/scores/${weekId}`);
        setResults(res.data);
      } catch (err) {
        console.error("Error loading results:", err);
      }
    };

    fetchResults();
  }, [weekId]);

  const handleDownloadPDF = () => {
    const element = document.getElementById("results-table");
    const opt = {
      margin: 0.5,
      filename: `weekly_results_week_${weekId}.pdf`,
      image: { type: "jpeg", quality: 0.98 },
      html2canvas: { scale: 2 },
      jsPDF: { unit: "in", format: "letter", orientation: "portrait" }
    };
    html2pdf().set(opt).from(element).save();
  };

  const handleWeekChange = (e) => {
    const newWeek = e.target.value;
    navigate(`/results/week/${newWeek}`);
  };

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-3xl font-bold">Weekly Results â€“ Week {weekId}</h1>

        <select
          value={weekId}
          onChange={handleWeekChange}
          className="border p-2 rounded ml-4"
        >
          <option value="1">Week 1</option>
          <option value="2">Week 2</option>
          <option value="3">Week 3</option>
          <option value="4">Week 4</option>
        </select>
      </div>

      <div className="text-right mb-4">
        <button
          onClick={handleDownloadPDF}
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
        >
          ðŸ“„ Download as PDF
        </button>
      </div>

      <div id="results-table">
        <table className="w-full border border-gray-400">
          <thead>
            <tr className="bg-gray-200">
              <th className="p-3 border text-left">#</th>
              <th className="p-3 border text-left">Player</th>
              <th className="p-3 border text-left">Score</th>
              <th className="p-3 border text-left">Praise</th>
            </tr>
          </thead>
          <tbody>
            {results.map((player, index) => (
              <tr key={index}>
                <td className="p-3 border">{index + 1}</td>
                <td className="p-3 border">{player.playerName}</td>
                <td className="p-3 border">{player.correct}</td>
                <td className="p-3 border text-yellow-600 text-xl">
                  {player.praise || "-"}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ResultsPage;
