import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const PrintableGamesPage = () => {
  const { week } = useParams();
  const navigate = useNavigate();
  const [games, setGames] = useState([]);
  const [selectedWeek, setSelectedWeek] = useState(week);

  useEffect(() => {
    if (!selectedWeek) return;

    fetch(`http://localhost:4000/api/games/${selectedWeek}`)
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setGames(data);
        } else {
          console.error('Data is not an array:', data);
          setGames([]);
        }
      })
      .catch((error) => {
        console.error('Failed to fetch games:', error);
        setGames([]);
      });
  }, [selectedWeek]);

  const handleWeekChange = (e) => {
    const newWeek = e.target.value;
    setSelectedWeek(newWeek);
    navigate(`/print/week/${newWeek}`);
  };

  const handlePrint = () => window.print();

  return (
    <div style={{ padding: '20px' }}>
      <h2>Printable Game List - Week {selectedWeek}</h2>

      <div style={{ marginBottom: '15px', display: 'flex', alignItems: 'center', gap: '10px' }}>
        <label htmlFor="week">Select Week:</label>
        <select id="week" value={selectedWeek} onChange={handleWeekChange}>
          <option value="1">Week 1</option>
          <option value="2">Week 2</option>
        </select>
        <button onClick={() => window.print()}>ðŸ¡’ Export to PDF</button>
        <button onClick={handlePrint}>Print This Page</button>
      </div>

      {games.length === 0 ? (
        <p>No games found for Week {selectedWeek}.</p>
      ) : (
        <ul style={{ lineHeight: '1.8em' }}>
          {games.map((game, index) => (
            <li key={index}>
              <strong>{game.date} at {game.time}</strong> â€” {game.team1} vs. {game.team2} ({game.spread})
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default PrintableGamesPage;
