// src/components/PrintableGamesPage.jsx
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import html2pdf from 'html2pdf.js';
import WeekSelector from './WeekSelector';

const PrintableGamesPage = () => {
  const { weekNumber } = useParams();
  const [games, setGames] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchGames = async () => {
      try {
        const res = await fetch(`http://localhost:4000/api/games/${weekNumber}`);
        const data = await res.json();
        if (Array.isArray(data)) {
          setGames(data);
        } else {
          console.error('Data is not an array:', data);
          setGames([]);
        }
      } catch (err) {
        console.error('Error fetching games:', err);
        setGames([]);
      }
    };

    fetchGames();
  }, [weekNumber]);

  const handleWeekChange = (newWeek) => {
    navigate(`/print/week/${newWeek}`);
  };

  const handlePrint = () => {
    window.print();
  };

  const handleExportToPDF = () => {
    const element = document.getElementById('printable-content');
    html2pdf().from(element).save(`Week${weekNumber}_Games.pdf`);
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h2 style={{ fontSize: '28px', marginBottom: '10px' }}>
        Printable Game List - Week {weekNumber}
      </h2>

      <div style={{ display: 'flex', alignItems: 'center', marginBottom: '20px', gap: '10px' }}>
        <label htmlFor="weekSelect">Select Week:</label>
        <WeekSelector selectedWeek={weekNumber} onChange={handleWeekChange} />
        <button onClick={handleExportToPDF} style={buttonStyle}>Export to PDF</button>
        <button onClick={handlePrint} style={buttonStyle}>Print This Page</button>
      </div>

      <div id="printable-content">
        {games.length === 0 ? (
          <p>No games found for Week {weekNumber}.</p>
        ) : (
          <ul style={{ paddingLeft: '20px' }}>
            {games.map((game, index) => (
              <li key={index} style={{ marginBottom: '8px' }}>
                <strong>{game.date}</strong> at <strong>{game.time}</strong> â€”&nbsp;
                <span>{game.team1} vs. {game.team2}</span> <em>({game.spread})</em>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

const buttonStyle = {
  padding: '6px 12px',
  backgroundColor: '#007bff',
  color: '#fff',
  border: 'none',
  borderRadius: '5px',
  cursor: 'pointer',
};

export default PrintableGamesPage;
