// React component: Weekly Scores (Tab 4)
import React, { useState } from 'react';

const WeeklyScoresPage = () => {
  const [selectedWeek, setSelectedWeek] = useState('Week 1');

  const scoresData = {
    'Week 1': [
      { team1: 'Roughriders', score1: 21, team2: 'Stampeders', score2: 17 },
      { team1: 'Bears', score1: 10, team2: 'Packers', score2: 24 },
    ],
    'Week 2': [],
  };

  const currentScores = scoresData[selectedWeek] || [];

  return (
    <div className="p-4 border rounded shadow bg-white">
      <h2 className="text-xl font-bold mb-4">📊 Weekly Scores</h2>

      <label className="block mb-2 font-semibold">📅 Select Week:</label>
      <select
        value={selectedWeek}
        onChange={(e) => setSelectedWeek(e.target.value)}
        className="border p-2 mb-4"
      >
        <option>Week 1</option>
        <option>Week 2</option>
        <option>Week 3</option>
        <option>Week 4</option>
        <option>Week 5</option>
        <option>Week 6</option>
        <option>Week 7</option>
        <option>Week 8</option>
        <option>Week 9</option>
        <option>Week 10</option>
        <option>Week 11</option>
        <option>Week 12</option>
        <option>Week 13</option>
        <option>Week 14</option>
        <option>Week 15</option>
        <option>Week 16</option>
      </select>

      {currentScores.length > 0 ? (
        <table className="w-full table-auto border">
          <thead>
            <tr>
              <th className="border px-2">Team 1</th>
              <th className="border px-2">Score</th>
              <th className="border px-2">Team 2</th>
              <th className="border px-2">Score</th>
            </tr>
          </thead>
          <tbody>
            {currentScores.map((game, index) => (
              <tr key={index}>
                <td className="border px-2">{game.team1}</td>
                <td className="border px-2">{game.score1}</td>
                <td className="border px-2">{game.team2}</td>
                <td className="border px-2">{game.score2}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p className="text-red-600 font-semibold mt-4">❗ No data found, please select a different week.</p>
      )}
    </div>
  );
};

export default WeeklyScoresPage;
