import React, { useEffect, useState } from 'react';
import { getCurrentWeek } from '../utils/getCurrentWeek';

const PlayerPicksPage = () => {
  const [games, setGames] = useState([]);
  const [selectedPicks, setSelectedPicks] = useState({});
  const [playerName, setPlayerName] = useState('');
  const [pin, setPin] = useState('');
  const [authorized, setAuthorized] = useState(false);
  const [message, setMessage] = useState('');
  const [currentWeek, setCurrentWeek] = useState(1);

  useEffect(() => {
    getCurrentWeek().then(week => {
      setCurrentWeek(week);
      fetch(`/data/games_week_${week}.json`)
        .then(res => res.json())
        .then(data => setGames(data))
        .catch(err => console.error('Error loading games:', err));
    });
  }, []);

  const handleTogglePick = (gameIndex, team) => {
    setSelectedPicks(prev => {
      if (prev[gameIndex] === team) {
        const updated = { ...prev };
        delete updated[gameIndex];
        return updated;
      } else {
        return { ...prev, [gameIndex]: team };
      }
    });
  };

  const handleLogin = () => {
    fetch('/api/authenticate', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ gameName: playerName, pin })
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          setAuthorized(true);
          setMessage('');
        } else {
          setMessage(data.error || 'Login failed.');
        }
      })
      .catch(() => setMessage('Error connecting to server.'));
  };

  const handleSubmit = () => {
    const picks = Object.values(selectedPicks);
    if (picks.length !== 10) {
      setMessage('You must select exactly 10 teams.');
      return;
    }

    fetch(`/submit-picks/${currentWeek}`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        player: playerName.trim(),
        pin: pin.trim(),
        picks,
        week: currentWeek
      })
    })
      .then(res => res.json())
      .then(data => setMessage(data.message || 'Picks submitted successfully!'))
      .catch(() => setMessage('Error submitting picks.'));
  };

  if (!authorized) {
    return (
      <div style={{ padding: '20px', maxWidth: '500px', margin: '0 auto' }}>
        <h2 style={{ fontSize: '20px', fontWeight: 'bold', marginBottom: '16px' }}>Enter to Make Your Picks</h2>
        <input
          type="text"
          placeholder="Game Name"
          value={playerName}
          onChange={(e) => setPlayerName(e.target.value)}
          style={{ display: 'block', marginBottom: '10px', padding: '8px', width: '100%' }}
        />
        <input
          type="password"
          placeholder="PIN"
          value={pin}
          onChange={(e) => setPin(e.target.value)}
          style={{ display: 'block', marginBottom: '16px', padding: '8px', width: '100%' }}
        />
        <button
          onClick={handleLogin}
          style={{ backgroundColor: '#007bff', color: '#fff', padding: '10px 20px', border: 'none', borderRadius: '4px' }}
        >
          Login
        </button>
        {message && <div style={{ marginTop: '12px', color: 'red' }}>{message}</div>}
      </div>
    );
  }

  return (
    <div style={{ padding: '20px', maxWidth: '900px', margin: '0 auto' }}>
      <h2 style={{ fontSize: '20px', fontWeight: 'bold', marginBottom: '16px' }}>
        Place Your Picks - Week {currentWeek}
      </h2>

      {games.map((game, idx) => (
        <div key={idx} style={{ borderBottom: '1px solid #ccc', paddingBottom: '10px', marginBottom: '12px' }}>
          <div style={{ fontSize: '14px', color: '#666', marginBottom: '4px' }}>
            {game.date} — {game.time}
          </div>
          <div style={{ display: 'flex', gap: '20px' }}>
            {[{ team: game.team1, spread: game.team1_spread }, { team: game.team2, spread: game.team2_spread }].map(({ team, spread }) => {
              const isSelected = selectedPicks[idx] === team;
              return (
                <button
                  key={team}
                  onClick={() => handleTogglePick(idx, team)}
                  style={{
                    padding: '6px 10px',
                    border: isSelected ? '3px solid black' : '1px solid gray',
                    backgroundColor: isSelected ? '#ffeb3b' : '#f0f0f0',
                    fontWeight: 'bold',
                    borderRadius: '4px',
                    cursor: 'pointer'
                  }}
                >
                  {spread} {team}
                </button>
              );
            })}
          </div>
        </div>
      ))}

      <div style={{ fontSize: '14px', color: '#007bff', fontWeight: '500', marginTop: '16px' }}>
        You have selected {Object.keys(selectedPicks).length} out of 10 games.
      </div>

      <button
        onClick={handleSubmit}
        style={{
          marginTop: '16px',
          padding: '10px 20px',
          backgroundColor: 'green',
          color: '#fff',
          border: 'none',
          borderRadius: '4px'
        }}
      >
        Submit Picks
      </button>

      <div style={{ marginTop: '12px', color: '#b76b00', fontWeight: 'bold' }}>
        Please double-check picks before submitting. Submission is final.
      </div>

      <div style={{ fontSize: '13px', color: '#444', marginTop: '8px' }}>
        You will be able to see your selections on the <strong>All Players Picks</strong> page.
        <br />
        Winning picks and scores will be revealed on Tuesday.
      </div>

      {message && <div style={{ marginTop: '16px', color: 'green', fontWeight: 'bold' }}>{message}</div>}
    </div>
  );
};

export default PlayerPicksPage;
