import React, { useState } from 'react';

const ChatPage = () => {
  const [name, setName] = useState('');
  const [message, setMessage] = useState('');
  const [chatLog, setChatLog] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!name.trim() || !message.trim()) {
      alert('Please enter both your name and message.');
      return;
    }
    const newMessage = {
      name,
      message,
      timestamp: new Date().toLocaleString()
    };
    setChatLog([newMessage, ...chatLog]);
    setMessage('');
  };

  return (
    <div style={{ padding: '30px', maxWidth: '700px', margin: 'auto', fontFamily: 'Arial, sans-serif' }}>
      <h2>üí¨ Pick'em Chat Room</h2>
      <p>Say hello, talk a little smack, or celebrate your winning week! üèÜ</p>

      <form onSubmit={handleSubmit} style={{ marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Your Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          style={{ width: '40%', padding: '8px', marginRight: '10px' }}
        />
        <input
          type="text"
          placeholder="Your Message"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          style={{ width: '40%', padding: '8px', marginRight: '10px' }}
        />
        <button type="submit">Send</button>
      </form>

      <div>
        {chatLog.length === 0 ? (
          <p>No messages yet. Be the first to say something!</p>
        ) : (
          chatLog.map((entry, index) => (
            <div key={index} style={{ borderBottom: '1px solid #ccc', padding: '10px 0' }}>
              <strong>{entry.name}</strong> <em style={{ fontSize: '0.8em' }}>({entry.timestamp})</em>
              <p>{entry.message}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default ChatPage;
