import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const PlayerPicksPage = () => {
  const { week } = useParams();
  const [selectedWeek, setSelectedWeek] = useState(parseInt(week) || 1);
  const [games, setGames] = useState([]);
  const [picks, setPicks] = useState([]);
  const [playerName, setPlayerName] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    console.log('Selected week is:', selectedWeek);
    fetch(`/api/games/week/${selectedWeek}`)
      .then(res => res.json())
      .then(data => setGames(data))
      .catch(err => console.error('Error fetching games:', err));
  }, [selectedWeek]);

  const handlePick = (team) => {
    if (picks.includes(team)) {
      setPicks(picks.filter(p => p !== team));
    } else if (picks.length < 10) {
      setPicks([...picks, team]);
    } else {
      alert('You must select exactly 10 teams.');
    }
  };

  const handleSubmit = () => {
    if (picks.length !== 10) {
      setMessage('You must select exactly 10 teams.');
      return;
    }

    fetch(`/api/picks/week/${selectedWeek}`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ name: playerName.trim(), picks }),
    })
      .then(res => res.json())
      .then(data => {
        setMessage(data.message || 'Picks submitted!');
        setPicks([]);
        setPlayerName('');
      })
      .catch(err => {
        console.error('Error submitting picks:', err);
        setMessage('Submission failed.');
      });
  };

  return (
    <div>
      <h1>Make Your Picks - Week</h1>

      <label>Select Week: </label>
      <select value={selectedWeek} onChange={(e) => setSelectedWeek(parseInt(e.target.value))}>
        {[1, 2, 3, 4, 5].map(w => (
          <option key={w} value={w}>Week {w}</option>
        ))}
      </select>

      <div>
        <a href={`/print/week/${selectedWeek}`} target="_blank" rel="noopener noreferrer">
          üñ®Ô∏è Print This Week's Games
        </a>
      </div>

      {games.map((game, index) => (
        <div key={index} style={{ margin: '10px 0' }}>
          <button
            onClick={() => handlePick(game.team1)}
            style={{
              backgroundColor: picks.includes(game.team1) ? '#d1e7dd' : 'white',
              marginRight: '10px',
            }}
          >
            {game.team1}
            {picks.includes(game.team1) && ' (Picked)'}
          </button>

          <span>
            vs ({game.spread})
          </span>

          <button
            onClick={() => handlePick(game.team2)}
            style={{
              backgroundColor: picks.includes(game.team2) ? '#d1e7dd' : 'white',
              marginLeft: '10px',
            }}
          >
            {game.team2}
            {picks.includes(game.team2) && ' (Picked)'}
          </button>
        </div>
      ))}

      <div>
        <input
          type="text"
          placeholder="Enter your game name"
          value={playerName}
          onChange={(e) => setPlayerName(e.target.value)}
        />
        <button onClick={handleSubmit}>Submit Picks</button>
      </div>

      {message && <p>{message}</p>}
    </div>
  );
};

export default PlayerPicksPage;
