import React, { useEffect, useState } from 'react';
import axios from 'axios';

function AdminApproveGamesPage() {
  const [uploadedGames, setUploadedGames] = useState([]);
  const [weekNumber, setWeekNumber] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    // Load the temporarily uploaded games
    axios.get('http://localhost:4000/data/uploadedGames.json')
      .then((res) => {
        setUploadedGames(res.data || []);
      })
      .catch((err) => {
        console.error('Error loading uploaded games:', err);
      });
  }, []);

  const handleApprove = () => {
    if (!weekNumber) {
      alert('Please enter a week number.');
      return;
    }

    axios.post(`http://localhost:4000/api/games/week/${weekNumber}`, uploadedGames)
      .then(() => {
        setMessage(`✅ Week ${weekNumber} games saved!`);
        setTimeout(() => setMessage(''), 3000);
      })
      .catch((err) => {
        console.error('Error saving approved games:', err);
        setMessage('❌ An error occurred while saving games.');
      });
  };

  return (
    <div className="p-4">
      <nav className="mb-4">
        <a href="/picks">Player Picks</a> |{' '}
        <a href="/results">Results</a> |{' '}
        <a href="/print">Printable</a> |{' '}
        <a href="/upload">Upload Games</a> |{' '}
        <a href="/approve">Approve Games</a>
      </nav>

      <h2 className="text-2xl font-bold mb-2">Approve Uploaded Games</h2>

      <div className="mb-4">
        <label>Week Number: </label>
        <input
          type="text"
          value={weekNumber}
          onChange={(e) => setWeekNumber(e.target.value)}
          className="border p-1 mr-2"
        />
        <button onClick={handleApprove} className="bg-blue-600 text-white px-4 py-1 rounded">
          Approve and Save
        </button>
      </div>

      {message && <div className="mb-4 text-green-700">{message}</div>}

      {uploadedGames.length === 0 ? (
        <p>No games uploaded yet.</p>
      ) : (
        <ul className="list-disc pl-6">
          {uploadedGames.map((game, index) => (
            <li key={index} className="mb-2">
              <strong>{game.date} – {game.time}</strong><br />
              {game.team1} ({game.team1Spread}) vs {game.team2} ({game.team2Spread})
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default AdminApproveGamesPage;
