import React, { useEffect, useState } from 'react';

const ScoredPicksPage = () => {
  const [picks, setPicks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [week, setWeek] = useState(1);
  const [winners, setWinners] = useState(Array(10).fill(''));

  useEffect(() => {
    fetch('http://localhost:4000/api/picks')
      .then((res) => res.json())
      .then((data) => {
        setPicks(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error('Error loading picks:', err);
        setLoading(false);
      });
  }, []);

  const handleWinnerChange = (index, value) => {
    const newWinners = [...winners];
    newWinners[index] = value;
    setWinners(newWinners);
  };

  const isCorrect = (playerPick, winners) => {
    return winners.includes(playerPick);
  };

  const getPlayerScore = (playerPicks) => {
    return playerPicks.filter((pick) => isCorrect(pick, winners)).length;
  };

  const filtered = picks.filter((entry) => entry.week === week);

  return (
    <div style={{ padding: '1rem', fontFamily: 'Arial' }}>
      <h1 style={{ fontSize: '24px', fontWeight: 'bold', marginBottom: '1rem' }}>
        Weekly Scoring
      </h1>

      <div style={{ marginBottom: '1rem' }}>
        <label style={{ fontWeight: 'bold', marginRight: '0.5rem' }}>Select Week:</label>
        <select
          value={week}
          onChange={(e) => setWeek(parseInt(e.target.value))}
          style={{ padding: '6px', borderRadius: '4px' }}
        >
          {[...Array(18)].map((_, i) => (
            <option key={i + 1} value={i + 1}>
              Week {i + 1}
            </option>
          ))}
        </select>
      </div>

      <div style={{ marginBottom: '1rem' }}>
        <h3 style={{ marginBottom: '0.5rem' }}>Enter 10 Winning Teams:</h3>
        {winners.map((team, index) => (
          <input
            key={index}
            type="text"
            placeholder={`Winner ${index + 1}`}
            value={team}
            onChange={(e) => handleWinnerChange(index, e.target.value)}
            style={{
              padding: '6px',
              marginRight: '0.5rem',
              marginBottom: '0.5rem',
              width: '120px',
            }}
          />
        ))}
      </div>

      {loading ? (
        <p>Loading submitted picks...</p>
      ) : filtered.length === 0 ? (
        <p>No picks submitted for Week {week}.</p>
      ) : (
        filtered.map((entry, index) => {
          const score = getPlayerScore(entry.picks);
          const praise =
            score >= 9 ? (
              <span style={{ color: 'gold', fontWeight: 'bold' }}>ðŸŒŸ Great job!</span>
            ) : null;

          return (
            <div
              key={index}
              style={{
                border: '1px solid #ccc',
                padding: '1rem',
                marginBottom: '1rem',
                borderRadius: '8px',
              }}
            >
              <h3 style={{ marginBottom: '0.5rem' }}>
                {entry.name} â€“ {score} / 10 correct {praise}
              </h3>
              <ul style={{ marginLeft: '1rem' }}>
                {entry.picks.map((team, i) => (
                  <li
                    key={i}
                    style={{
                      color: isCorrect(team, winners) ? 'green' : 'red',
                      fontWeight: isCorrect(team, winners) ? 'bold' : 'normal',
                    }}
                  >
                    {team}
                  </li>
                ))}
              </ul>
            </div>
          );
        })
      )}
    </div>
  );
};

export default ScoredPicksPage;
