import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const PrintableGamesPage = () => {
  const { week } = useParams();
  const [games, setGames] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchGames = async () => {
      try {
        const response = await fetch(`http://localhost:4000/api/games/${week}`);
        const data = await response.json();
        setGames(data);
      } catch (error) {
        console.error('Error fetching games:', error);
      }
    };

    fetchGames();
  }, [week]);

  const handleWeekChange = (e) => {
    navigate(`/print/week/${e.target.value}`);
  };

  const handlePrint = () => {
    window.print();
  };

  return (
    <div className="p-6 print:p-0 print:mt-4">

      {/* Top Header (Visible on Screen and Print) */}
      <div className="text-center mb-6">
        <h1 className="text-3xl font-bold">üñ®Ô∏è The Spread ‚Äì Week {week}</h1>
      </div>

      {/* Controls - Only Show On Screen */}
      <div className="flex justify-center print:hidden mb-4 gap-2">
        <select value={week} onChange={handleWeekChange} className="border px-2 py-1 rounded">
          {[...Array(17)].map((_, i) => (
            <option key={i + 1} value={i + 1}>
              Week {i + 1}
            </option>
          ))}
        </select>
        <button onClick={handlePrint} className="border px-3 py-1 rounded bg-gray-100 hover:bg-gray-200">
          Print Page
        </button>
      </div>

      {/* Game List */}
      <ol className="list-decimal pl-6 text-lg">
        {games.map((game, index) => (
          <li key={index} className="mb-4">
            <div>
              <strong>
                {game.team1} ({game.spread}) vs {game.team2}
              </strong>
            </div>
            <div>
              Date: {game.date} | Time: {game.time}
            </div>
          </li>
        ))}
      </ol>
    </div>
  );
};

export default PrintableGamesPage;
