import React, { useEffect, useState } from 'react';

const PlayerPicksPage = () => {
  const [games, setGames] = useState([]);
  const [selectedPicks, setSelectedPicks] = useState({});
  const [playerName, setPlayerName] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('http://localhost:4000/api/games')
      .then((res) => res.json())
      .then((data) => {
        setGames(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching games:', error);
        setLoading(false);
      });
  }, []);

  const handlePick = (gameIndex, team) => {
    const newPicks = { ...selectedPicks, [gameIndex]: team };

    const totalPicks = Object.keys(newPicks).length;
    if (totalPicks > 10) {
      alert('You can only select 10 teams total.');
      return;
    }

    setSelectedPicks(newPicks);
  };

  const handleSubmit = () => {
    if (!playerName.trim()) {
      alert('Please enter your game name.');
      return;
    }

    if (Object.keys(selectedPicks).length !== 10) {
      alert('Please select exactly 10 picks before submitting.');
      return;
    }

    const submission = {
      name: playerName,
      picks: Object.values(selectedPicks),
    };

    fetch('http://localhost:4000/api/submit-picks', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(submission),
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error('Submission failed');
        }
        return res.json();
      })
      .then((data) => {
        alert(data.message);
      })
      .catch((err) => {
        console.error(err);
        alert('Something went wrong submitting your picks.');
      });
  };

  if (loading) {
    return <div className="p-4 text-lg">Loading games...</div>;
  }

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Player Picks - Week 1</h1>

      <div className="mb-4">
        <label className="block mb-1 font-medium">Your Game Name:</label>
        <input
          type="text"
          value={playerName}
          onChange={(e) => setPlayerName(e.target.value)}
          className="border p-2 w-full max-w-sm rounded"
          placeholder="Enter your exact game name"
        />
      </div>

      <p className="mb-2">Selected Picks: {Object.keys(selectedPicks).length} / 10</p>

      <div className="grid gap-4 mb-6">
        {games.map((game, index) => {
          const spread = parseFloat(game.spread);
          let displayText = '';

          if (spread < 0) {
            displayText = `${game.team1} (${spread}) vs ${game.team2}`;
          } else if (spread > 0) {
            displayText = `${game.team1} vs ${game.team2} (-${spread})`;
          } else {
            displayText = `${game.team1} vs ${game.team2}`;
          }

          return (
            <div key={index} className="border p-4 rounded shadow-md">
              <p className="text-lg font-medium mb-2">{displayText}</p>
              <div className="flex gap-4">
                {[game.team1, game.team2].map((team) => (
                  <button
                    key={team}
                    onClick={() => handlePick(index, team)}
                    className={`px-4 py-2 rounded border ${
                      selectedPicks[index] === team
                        ? 'bg-blue-500 text-white font-bold'
                        : 'bg-gray-100 hover:bg-gray-200'
                    }`}
                  >
                    {team}
                  </button>
                ))}
              </div>
            </div>
          );
        })}
      </div>

      <button
        onClick={handleSubmit}
        className="bg-green-600 hover:bg-green-700 text-white px-6 py-2 rounded font-bold"
      >
        Submit Picks
      </button>
    </div>
  );
};

export default PlayerPicksPage;
