import React, { useState, useEffect } from 'react';
import axios from 'axios';

const isBeforeDeadline = () => {
  const now = new Date();
  const deadline = new Date();
  deadline.setHours(12, 0, 0, 0); // 12:00 PM
  return now.getDay() <= 5 && now < deadline; // Friday noon or earlier
};

const PlayerPicksPage = () => {
  const [games, setGames] = useState([]);
  const [selectedTeams, setSelectedTeams] = useState([]);
  const [playerName, setPlayerName] = useState('');
  const [submitted, setSubmitted] = useState(false);
  const [message, setMessage] = useState('');
  const [canSubmit, setCanSubmit] = useState(true);

  useEffect(() => {
    axios.get('http://localhost:4000/api/games')
      .then(res => setGames(res.data))
      .catch(err => console.error('Error fetching games:', err));

    // Check lock
    setCanSubmit(isBeforeDeadline());
  }, []);


  const handlePick = (team) => {
    if (submitted) return;

    if (selectedTeams.includes(team)) {
      setSelectedTeams(selectedTeams.filter(t => t !== team));
    } else {
      if (selectedTeams.length < 10) {
        setSelectedTeams([...selectedTeams, team]);
        setMessage('');
      } else {
        setMessage('You can only select 10 teams.');
      }
    }
  };

  const handleSubmit = () => {
    if (submitted) return;

    if (!playerName.trim()) {
      setMessage('Please enter your game name before submitting.');
      return;
    }

    if (selectedTeams.length !== 10) {
      setMessage(`You have selected ${selectedTeams.length} teams. You must select exactly 10.`);
      return;
    }

    axios.post('http://localhost:4000/api/submit-picks', {
      name: playerName.trim(),
      picks: selectedTeams
    }).then(() => {
      setSubmitted(true);
      setMessage('✅ Your picks have been successfully submitted.');
    }).catch(err => {
      console.error('Error submitting picks:', err);
      setMessage('❌ There was a problem submitting your picks. Try again later.');
    });
  };

  return (
    <div className="p-4 max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold mb-2">Weekly Picks</h2>
      <p className="mb-4">
        Select exactly 10 teams by clicking the “Pick” button. Once done, press Enter (Submit).
        You will not be able to change your picks after submission.
      </p>

      <input
        type="text"
        placeholder="Enter your game name"
        value={playerName}
        onChange={(e) => setPlayerName(e.target.value)}
        disabled={submitted}
        className="mb-4 p-2 border rounded w-full"
      />

      <table className="w-full border mb-4">
        <thead>
          <tr className="bg-gray-200">
            <th className="p-2 border">Date</th>
            <th className="p-2 border">Time</th>
            <th className="p-2 border">Teams & Spread</th>
            <th className="p-2 border">Pick</th>
          </tr>
        </thead>
        <tbody>
          {games.map((game, index) => (
            <tr key={index} className="text-center">
              <td className="border p-2">{game.date}</td>
              <td className="border p-2">{game.time}</td>
              <td className="border p-2">
                {game.awayTeam} ({game.spread}) @ {game.homeTeam}
              </td>
              <td className="border p-2 flex flex-col gap-1">
                {[game.awayTeam, game.homeTeam].map((team) => (
                  <button
                    key={team}
                    onClick={() => handlePick(team)}
                    disabled={submitted || !canSubmit}
                    className={`px-3 py-1 rounded ${
                      selectedTeams.includes(team)
                        ? 'bg-green-500 text-white'
                        : 'bg-gray-200'
                    }`}
                  >
                    {selectedTeams.includes(team) ? 'Picked' : 'Pick'} {team}
                  </button>
                ))}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <button
        onClick={handleSubmit}
        disabled={submitted}
        className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
      >
        Submit Picks
      </button>

      {message && <p className="mt-4 text-red-600">{message}</p>}
    </div>
  );
};

export default PlayerPicksPage;
