// React component: Rules Page (Tab 7)
import React from 'react';

const RulesPage = () => {
  return (
    <div className="p-4 border rounded shadow bg-white">
      <h2 className="text-xl font-bold mb-4">ðŸ“˜ Game Rules</h2>

      <ul className="list-disc list-inside space-y-2">
        <li>Each player must select exactly <strong>10 teams</strong> per week.</li>
        <li>Selections must be submitted by <strong>Thursday at 1:00 PM CST</strong>.</li>
        <li>Games include both the <strong>CFL</strong> and the <strong>NFL</strong>.</li>
        <li>You may select any 10 teams from the available weekly spread.</li>
        <li>Once submitted, picks <strong>cannot be changed</strong>.</li>
        <li>Each correct pick earns <strong>1 point</strong>; incorrect picks earn 0 points.</li>
        <li>The cumulative score is tracked and shown throughout the season.</li>
        <li>Players with <strong>9 out of 10 correct</strong> in a single week will receive a <strong>gold star</strong> and a praise message.</li>
        <li>If no picks are submitted by the deadline, the player will receive <strong>zero points</strong> for that week.</li>
        <li>All picks are visible to everyone after the deadline.</li>
      </ul>

      <p className="mt-4 text-sm text-gray-600">For questions or clarifications, contact the game administrator.</p>
    </div>
  );
};

export default RulesPage;
