import React, { useEffect, useState } from 'react';

const AdminPicksPage = () => {
  const [picks, setPicks] = useState([]);
  const [filteredWeek, setFilteredWeek] = useState(1);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('http://localhost:4000/api/picks')
      .then((res) => res.json())
      .then((data) => {
        setPicks(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error('Error loading picks:', err);
        setLoading(false);
      });
  }, []);

  const filtered = picks.filter((entry) => entry.week === filteredWeek);

  return (
    <div style={{ padding: '1rem', fontFamily: 'Arial' }}>
      <h1 style={{ fontSize: '24px', fontWeight: 'bold', marginBottom: '1rem' }}>
        Admin Picks Viewer
      </h1>

      <div style={{ marginBottom: '1rem' }}>
        <label style={{ fontWeight: 'bold', marginRight: '0.5rem' }}>Select Week:</label>
        <select
          value={filteredWeek}
          onChange={(e) => setFilteredWeek(parseInt(e.target.value))}
          style={{ padding: '6px', borderRadius: '4px' }}
        >
          {[...Array(18)].map((_, i) => (
            <option key={i + 1} value={i + 1}>
              Week {i + 1}
            </option>
          ))}
        </select>
      </div>

      {loading ? (
        <p>Loading submitted picks...</p>
      ) : filtered.length === 0 ? (
        <p>No picks submitted for Week {filteredWeek}.</p>
      ) : (
        filtered.map((entry, index) => (
          <div
            key={index}
            style={{
              border: '1px solid #ccc',
              padding: '1rem',
              marginBottom: '1rem',
              borderRadius: '8px'
            }}
          >
            <h3 style={{ marginBottom: '0.5rem' }}>
              {entry.name} –{' '}
              <span style={{ fontSize: '14px', color: '#555' }}>
                {new Date(entry.timestamp).toLocaleString()}
              </span>
            </h3>
            <ul style={{ marginLeft: '1rem' }}>
              {entry.picks.map((team, i) => (
                <li key={i}>• {team}</li>
              ))}
            </ul>
          </div>
        ))
      )}
    </div>
  );
};

export default AdminPicksPage;
