import React, { useState } from "react";

function AdminUploadPage() {
  const [spreadText, setSpreadText] = useState("");
  const [message, setMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    const res = await fetch("http://localhost:4000/api/admin/upload", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ rawSpread: spreadText }),
    });

    const data = await res.json();

    if (res.ok) {
      setMessage("‚úÖ Spread uploaded successfully!");
      setSpreadText("");
    } else {
      setMessage("‚ùå Error uploading spread: " + data.error);
    }
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial" }}>
      <h2>üì§ Admin: Upload Weekly Spread</h2>
      <form onSubmit={handleSubmit}>
        <textarea
          rows={20}
          cols={80}
          value={spreadText}
          onChange={(e) => setSpreadText(e.target.value)}
          placeholder="Paste weekly spread here..."
        ></textarea>
        <br />
        <button type="submit">Upload Spread</button>
      </form>
      {message && <p style={{ marginTop: "1rem" }}>{message}</p>}
    </div>
  );
}

export default AdminUploadPage;
