import React, { useEffect, useState } from "react";
import axios from "axios";

const PlayerPicksPage = () => {
  const [games, setGames] = useState([]);
  const [selectedTeams, setSelectedTeams] = useState([]);
  const [name, setName] = useState("");
  const [warning, setWarning] = useState("");

  useEffect(() => {
    axios.get("http://localhost:4000/api/games")
      .then((res) => setGames(res.data))
      .catch((err) => console.error("Error loading games:", err));
  }, []);

  const toggleTeam = (team) => {
    if (selectedTeams.includes(team)) {
      setSelectedTeams(selectedTeams.filter(t => t !== team));
    } else {
      if (selectedTeams.length < 10) {
        setSelectedTeams([...selectedTeams, team]);
      } else {
        setWarning("You can only select 10 teams.");
      }
    }
  };

  const handleSubmit = () => {
    if (selectedTeams.length !== 10) {
      setWarning("Please select exactly 10 teams.");
      return;
    }
    if (!name.trim()) {
      setWarning("Please enter your game name.");
      return;
    }

    axios.post("http://localhost:4000/api/picks", {
      name,
      picks: selectedTeams,
      timestamp: new Date().toISOString()
    })
      .then(() => {
        alert("Picks submitted successfully!");
        setSelectedTeams([]);
        setName("");
        setWarning("");
      })
      .catch((err) => {
        console.error("Error submitting picks:", err);
        setWarning("Error submitting picks.");
      });
  };

  return (
    <div>
      <h1>Make Your Picks</h1>
      <input
        type="text"
        placeholder="Enter your game name"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
      <div style={{ margin: "20px 0" }}>
        {games.map((game, index) => (
          <div key={index}>
            <label>
              <input
                type="checkbox"
                checked={selectedTeams.includes(game.team1)}
                onChange={() => toggleTeam(game.team1)}
              />
              {game.team1} ({game.spread})
            </label>
            {" vs "}
            <label>
              <input
                type="checkbox"
                checked={selectedTeams.includes(game.team2)}
                onChange={() => toggleTeam(game.team2)}
              />
              {game.team2}
            </label>
          </div>
        ))}
      </div>

      {warning && <p style={{ color: "red" }}>{warning}</p>}

      <button onClick={handleSubmit}>Submit Picks</button>
    </div>
  );
};

export default PlayerPicksPage;
