import React, { useState } from 'react';

const AdminToolsPage = () => {
  const [authenticated, setAuthenticated] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [selectedFiles, setSelectedFiles] = useState({});

  const handleLogin = () => {
    if (username === 'Admin' && password === 'letmein') {
      setAuthenticated(true);
    } else {
      alert('Invalid credentials.');
    }
  };

  const handleFileChange = (e, key) => {
    setSelectedFiles(prev => ({ ...prev, [key]: e.target.files[0] }));
  };

  const handleUpload = (key, endpoint) => {
    const file = selectedFiles[key];
    if (!file) return alert('Please select a file.');
    const formData = new FormData();
    formData.append('file', file);

    fetch(endpoint, {
      method: 'POST',
      body: formData
    })
      .then(res => res.json())
      .then(data => alert(data.message || 'File uploaded!'))
      .catch(() => alert('Upload failed.'));
  };

  if (!authenticated) {
    return (
      <div className="p-4 max-w-sm mx-auto">
        <h2 className="text-xl font-bold mb-4">Admin Login</h2>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={e => setUsername(e.target.value)}
          className="block w-full mb-2 px-2 py-1 border rounded"
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={e => setPassword(e.target.value)}
          className="block w-full mb-4 px-2 py-1 border rounded"
        />
        <button
          onClick={handleLogin}
          className="bg-blue-600 text-white px-4 py-2 rounded"
        >
          Login
        </button>
      </div>
    );
  }

  return (
    <div className="p-4 max-w-xl mx-auto">
      <h2 className="text-xl font-bold mb-6">Administration Page</h2>

      <UploadSection
        label="Upload Game Spread"
        fileKey="spread"
        endpoint="/upload-spread"
        onFileChange={handleFileChange}
        onUpload={handleUpload}
        file={selectedFiles.spread}
      />

      <UploadSection
        label="Upload Game Scores"
        fileKey="scores"
        endpoint="/upload-scores"
        onFileChange={handleFileChange}
        onUpload={handleUpload}
        file={selectedFiles.scores}
      />

      <UploadSection
        label="Upload Player Roster"
        fileKey="roster"
        endpoint="/upload-roster"
        onFileChange={handleFileChange}
        onUpload={handleUpload}
        file={selectedFiles.roster}
      />
    </div>
  );
};

const UploadSection = ({ label, fileKey, endpoint, onFileChange, onUpload, file }) => (
  <div className="mb-6">
    <p className="font-semibold mb-1">{label}</p>
    <input type="file" onChange={e => onFileChange(e, fileKey)} className="mb-2" />
    <button
      onClick={() => onUpload(fileKey, endpoint)}
      className="bg-green-600 text-white px-3 py-1 rounded"
    >
      Import File to JSON
    </button>
  </div>
);

export default AdminToolsPage;
