// frontend/src/components/ViewScoresPage.jsx

import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

function ViewScoresPage() {
  const { weekNumber } = useParams();
  const navigate = useNavigate();
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchSpreadWinners = async () => {
      try {
        const res = await fetch(`/api/calculate-winners/${weekNumber}`);
        const data = await res.json();

        if (res.ok) {
          setResults(data.results);
        } else {
          setError(data.message || "Failed to load results.");
        }
      } catch (err) {
        setError("Error fetching spread winners.");
      } finally {
        setLoading(false);
      }
    };

    fetchSpreadWinners();
  }, [weekNumber]);

  const handleWeekChange = (e) => {
    navigate(`/scores/week/${e.target.value}`);
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Week {weekNumber} Scores & Spread Winners</h2>

      <label>
        Select Week:
        <select value={weekNumber} onChange={handleWeekChange} style={{ marginLeft: "10px" }}>
          {Array.from({ length: 17 }, (_, i) => (
            <option key={i + 1} value={i + 1}>
              Week {i + 1}
            </option>
          ))}
        </select>
      </label>

      {loading ? (
        <p>Loading spread results...</p>
      ) : error ? (
        <p style={{ color: "red" }}>{error}</p>
      ) : (
        <ul style={{ listStyle: "none", padding: 0, marginTop: "20px" }}>
          {results.map((game, idx) => (
            <li
              key={idx}
              style={{
                marginBottom: "12px",
                backgroundColor: "#f9f9f9",
                padding: "10px",
                borderRadius: "8px",
                border: "1px solid #ccc",
              }}
            >
              {game.description} ‚Üí üèÜ <strong>{game.winner}</strong>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default ViewScoresPage;
