import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const IntroPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [authenticated, setAuthenticated] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = () => {
    if (username === 'Popcorn' && password === '&Peanuts!') {
      setAuthenticated(true);
      setError('');
    } else {
      setError('Incorrect username or password.');
    }
  };

  if (!authenticated) {
    return (
      <div style={{ padding: '40px', maxWidth: '400px', margin: '0 auto' }}>
        <h2 style={{ fontSize: '20px', fontWeight: 'bold', marginBottom: '20px' }}>Welcome to the Football Pick'em Game</h2>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={e => setUsername(e.target.value)}
          style={{ display: 'block', marginBottom: '10px', padding: '8px', width: '100%' }}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={e => setPassword(e.target.value)}
          style={{ display: 'block', marginBottom: '16px', padding: '8px', width: '100%' }}
        />
        <button
          onClick={handleLogin}
          style={{ backgroundColor: '#007bff', color: '#fff', padding: '10px 20px', border: 'none', borderRadius: '4px' }}
        >
          Login
        </button>
        {error && <div style={{ marginTop: '12px', color: 'red' }}>{error}</div>}
      </div>
    );
  }

  return (
    <div style={{ padding: '20px', textAlign: 'center' }}>
      <h1 style={{ fontSize: '26px', marginBottom: '20px' }}>Welcome to the Football Pick'em Game</h1>
      <div>
        {[
          { label: 'Place Your Picks Here', path: '/picks' },
          { label: 'All Players\' Picks Page', path: '/allpicks' },
          { label: 'The Spread by Week', path: '/spread' },
          { label: 'Weekly Scores', path: '/weekly-scores' },
          { label: 'Players Roster', path: '/roster-payments' },
          { label: 'Administration Tools', path: '/admin-tools' },
          { label: 'Rules', path: '/rules' },
          { label: 'Chat', path: '/chat' }
        ].map(({ label, path }) => (
          <button
            key={label}
            onClick={() => navigate(path)}
            style={{
              margin: '4px',
              padding: '8px 12px',
              border: '1px solid #333',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            {label}
          </button>
        ))}
      </div>
    </div>
  );
};

export default IntroPage;
