import React, { useEffect, useState } from "react";

const PlayerPicksPage = () => {
  const [games, setGames] = useState([]);
  const [selectedTeams, setSelectedTeams] = useState([]);
  const [playerName, setPlayerName] = useState("");
  const [message, setMessage] = useState("");

  useEffect(() => {
    fetch("http://localhost:4000/api/games")
      .then((res) => res.json())
      .then((data) => setGames(data))
      .catch((err) => console.error("Failed to fetch games", err));
  }, []);

  const toggleTeam = (team) => {
    if (selectedTeams.includes(team)) {
      setSelectedTeams(selectedTeams.filter((t) => t !== team));
    } else {
      if (selectedTeams.length < 10) {
        setSelectedTeams([...selectedTeams, team]);
      } else {
        alert("You can only pick 10 teams.");
      }
    }
  };

  const handleSubmit = async () => {
    if (!playerName.trim()) {
      setMessage("Please enter your game name.");
      return;
    }
    if (selectedTeams.length !== 10) {
      setMessage("You must pick exactly 10 teams.");
      return;
    }

    const pickData = {
      name: playerName,
      picks: selectedTeams,
      timestamp: new Date().toISOString(),
    };

    try {
      const response = await fetch("http://localhost:4000/api/picks", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(pickData),
      });

      if (response.ok) {
        setMessage("Picks submitted successfully!");
        setSelectedTeams([]);
        setPlayerName("");
      } else {
        setMessage("Failed to submit picks.");
      }
    } catch (error) {
      console.error("Error submitting picks", error);
      setMessage("Something went wrong.");
    }
  };

  return (
    <div>
      <h1>Make Your Picks</h1>
      <input
        type="text"
        placeholder="Enter your game name"
        value={playerName}
        onChange={(e) => setPlayerName(e.target.value)}
      />
      <div>
        {games.map((game, index) => (
          <div key={index}>
            <label>
              <input
                type="checkbox"
                checked={selectedTeams.includes(game.team1)}
                onChange={() => toggleTeam(game.team1)}
              />
              {game.team1} ({game.spread > 0 ? "+" : ""}
              {game.spread})
            </label>
            <label style={{ marginLeft: "20px" }}>
              <input
                type="checkbox"
                checked={selectedTeams.includes(game.team2)}
                onChange={() => toggleTeam(game.team2)}
              />
              {game.team2} ({game.spread < 0 ? "+" : ""}
              {-game.spread})
            </label>
          </div>
        ))}
      </div>
      <button onClick={handleSubmit}>Submit Picks</button>
      <p style={{ color: "red" }}>{message}</p>
    </div>
  );
};

export default PlayerPicksPage;
