// React component: Chat Page (Tab 8 - basic message board style)
import React, { useState } from 'react';

const ChatPage = () => {
  const [messages, setMessages] = useState([
    { user: 'Alice', text: 'Good luck everyone this week!' },
    { user: 'Bob', text: 'BlitzQueen is coming for the crown!' },
  ]);
  const [newUser, setNewUser] = useState('');
  const [newMessage, setNewMessage] = useState('');

  const handleSubmit = () => {
    if (newUser && newMessage) {
      setMessages([...messages, { user: newUser, text: newMessage }]);
      setNewMessage('');
    }
  };

  return (
    <div className="p-4 border rounded shadow bg-white">
      <h2 className="text-xl font-bold mb-4">ðŸ’¬ Chat Board</h2>

      <div className="space-y-2 mb-4 max-h-64 overflow-y-auto border p-2 bg-gray-50">
        {messages.map((msg, index) => (
          <div key={index} className="border-b pb-1">
            <strong>{msg.user}:</strong> {msg.text}
          </div>
        ))}
      </div>

      <div className="mb-2">
        <input
          type="text"
          placeholder="Your name"
          value={newUser}
          onChange={(e) => setNewUser(e.target.value)}
          className="border p-2 mr-2"
        />
        <input
          type="text"
          placeholder="Your message"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          className="border p-2 mr-2 w-1/2"
        />
        <button onClick={handleSubmit} className="bg-blue-600 text-white px-4 py-2 rounded">
          Post
        </button>
      </div>
    </div>
  );
};

export default ChatPage;
