import React, { useEffect, useState } from 'react';

const RosterPaymentsPage = () => {
  const [roster, setRoster] = useState([]);

  useEffect(() => {
    fetch('/data/roster.json')
      .then(res => res.json())
      .then(data => setRoster(data))
      .catch(err => {
        console.error('Error loading roster:', err);
        setRoster([]);
      });
  }, []);

  return (
    <div style={{
      padding: '20px',
      maxWidth: '800px',
      margin: '0 auto',
      background: 'linear-gradient(#3c9d3c, #215c21)',
      borderRadius: '15px',
      boxShadow: '0 4px 10px rgba(0, 0, 0, 0.2)',
      color: 'white',
      fontFamily: 'Arial, sans-serif',
      border: '6px solid #ffffff'
    }}>
      <div style={{
        backgroundColor: '#000000',
        color: '#00FF00',
        fontFamily: 'monospace',
        fontSize: '28px',
        fontWeight: 'bold',
        textAlign: 'center',
        padding: '16px',
        borderRadius: '8px',
        marginBottom: '24px',
        border: '3px dashed #FFD700'
      }}>
        üèüÔ∏è SHAMALAMADINGDONG SCOREBOARD ‚Äì PLAYERS ROSTER üèà
      </div>

      <div style={{ textAlign: 'center', fontSize: '60px', marginBottom: '10px' }}>ü•Ö</div>

      {roster.length === 0 ? (
        <p style={{ textAlign: 'center', color: '#fff' }}>No roster data available.</p>
      ) : (
        <div>
          {roster.map((player, idx) => (
            <div
              key={idx}
              style={{
                display: 'flex',
                alignItems: 'center',
                padding: '14px 20px',
                backgroundColor: idx % 2 === 0 ? 'rgba(255, 255, 255, 0.1)' : 'rgba(255, 255, 255, 0.15)',
                borderRadius: '8px',
                marginBottom: '10px',
                fontSize: '20px',
                fontWeight: 'bold',
                boxShadow: '1px 1px 4px rgba(0,0,0,0.2)'
              }}
            >
              üß¢ {player.GameName?.trim() || 'Unnamed Player'} <span style={{ marginLeft: 'auto' }}>üèà üëï</span>
            </div>
          ))}
        </div>
      )}

      <div style={{ textAlign: 'center', fontSize: '60px', marginTop: '10px' }}>ü•Ö</div>
    </div>
  );
};

export default RosterPaymentsPage;
