import React, { useState } from "react";
import * as XLSX from "xlsx";

const AdminUploadTool = () => {
  const [games, setGames] = useState([]);

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const data = await file.arrayBuffer();
    const workbook = XLSX.read(data);
    const sheet = workbook.Sheets[workbook.SheetNames[0]];
    const raw = XLSX.utils.sheet_to_json(sheet, { header: 1 });

    const parsedGames = [];

    for (let i = 0; i < raw.length - 5; i++) {
      const dateRow = raw[i]?.[0];
      const timeRow = raw[i + 1]?.[0];
      const teamARow = raw[i + 2];
      const teamBRow = raw[i + 3];

      if (
        typeof dateRow === "string" &&
        typeof timeRow === "string" &&
        Array.isArray(teamARow) &&
        Array.isArray(teamBRow)
      ) {
        const date = dateRow.trim();
        const time = timeRow.trim();
        const teamA = teamARow[1]?.trim() || "";
        const spreadA = teamARow[2]?.replace("½", ".5").split(" ")[0] || "";
        const teamB = teamBRow[1]?.trim() || "";
        const spreadB = teamBRow[2]?.replace("½", ".5").split(" ")[0] || "";

        if (teamA && teamB && spreadA && spreadB) {
          parsedGames.push({ date, time, teamA, spreadA, teamB, spreadB });
          i += 5; // skip to the next game block
        }
      }
    }

    setGames(parsedGames);
  };

  const handleSave = () => {
    console.log("Saved Games:", games);
    alert("Games saved to backend (console only for now)");
  };

  return (
    <div className="p-4 max-w-5xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Admin Upload: Weekly Game List</h1>
      <input
        type="file"
        accept=".xlsx"
        onChange={handleFileUpload}
        className="mb-4"
      />

      {games.length > 0 && (
        <>
          <table className="table-auto w-full border mb-4">
            <thead>
              <tr>
                <th>Date</th>
                <th>Time</th>
                <th>Team A</th>
                <th>Spread A</th>
                <th>Team B</th>
                <th>Spread B</th>
              </tr>
            </thead>
            <tbody>
              {games.map((game, idx) => (
                <tr key={idx}>
                  <td>{game.date}</td>
                  <td>{game.time}</td>
                  <td>{game.teamA}</td>
                  <td>{game.spreadA}</td>
                  <td>{game.teamB}</td>
                  <td>{game.spreadB}</td>
                </tr>
              ))}
            </tbody>
          </table>
          <button
            onClick={handleSave}
            className="bg-green-600 text-white px-4 py-2 rounded"
          >
            Save Week
          </button>
        </>
      )}
    </div>
  );
};

export default AdminUploadTool;
