import React, { useState, useEffect } from "react";
import axios from "axios";

const PlayerPicksPage = () => {
  const [name, setName] = useState("");
  const [games, setGames] = useState([]);
  const [picks, setPicks] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:4000/api/games")
      .then((res) => setGames(res.data))
      .catch((err) => console.error("Failed to fetch games:", err));
  }, []);

  const togglePick = (team) => {
    setPicks((prev) =>
      prev.includes(team) ? prev.filter((t) => t !== team) : [...prev, team]
    );
  };

  const handleSubmit = () => {
    if (picks.length !== 10) {
      alert("Please select exactly 10 picks.");
      return;
    }

    axios
      .post("http://localhost:4000/api/picks", {
        name,
        picks,
        timestamp: new Date().toISOString(),
      })
      .then(() => {
        alert("Picks submitted!");
        setPicks([]);
        setName("");
      })
      .catch((err) => alert("Failed to submit picks:", err));
  };

  return (
    <div>
      <h1>Make Your Picks</h1>
      <input
        placeholder="Enter your game name"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
      <div style={{ marginTop: "20px" }}>
        {games.map((game, index) => (
          <div key={index}>
            <button
              onClick={() => togglePick(game.team1)}
              style={{
                backgroundColor: picks.includes(game.team1) ? "lightgreen" : "",
              }}
            >
              {game.team1} ({game.spread})
            </button>{" "}
            vs.{" "}
            <button
              onClick={() => togglePick(game.team2)}
              style={{
                backgroundColor: picks.includes(game.team2) ? "lightgreen" : "",
              }}
            >
              {game.team2}
            </button>
          </div>
        ))}
      </div>
      <br />
      <button onClick={handleSubmit}>Submit Picks</button>
    </div>
  );
};

export default PlayerPicksPage;
