import React, { useState } from 'react';
import './AdminUploadPage.css';

function AdminUploadPage() {
  const [selectedWeek, setSelectedWeek] = useState(1);
  const [gameData, setGameData] = useState('');
  const [winnerData, setWinnerData] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [password, setPassword] = useState('');

  const correctPassword = 'football2025'; // change this as needed

  const handleWeekChange = (e) => {
    setSelectedWeek(e.target.value);
  };

  const handleGameDataUpload = async () => {
    try {
      const response = await fetch(`/api/upload-games/${selectedWeek}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: gameData,
      });

      if (response.ok) {
        alert('Game data uploaded successfully!');
        setGameData('');
      } else {
        alert('Failed to upload game data.');
      }
    } catch (error) {
      console.error('Error uploading game data:', error);
      alert('Error uploading game data.');
    }
  };

  const handleWinnerDataUpload = async () => {
    try {
      const response = await fetch(`/api/upload-winners/${selectedWeek}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: winnerData,
      });

      if (response.ok) {
        alert('Winner data uploaded successfully!');
        setWinnerData('');
      } else {
        alert('Failed to upload winner data.');
      }
    } catch (error) {
      console.error('Error uploading winner data:', error);
      alert('Error uploading winner data.');
    }
  };

  const handleLogin = () => {
    if (password === correctPassword) {
      setIsAuthenticated(true);
    } else {
      alert('Incorrect password.');
    }
  };

  if (!isAuthenticated) {
    return (
      <div style={{ padding: '20px' }}>
        <h2>Admin Access</h2>
        <input
          type="password"
          placeholder="Enter admin password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button onClick={handleLogin}>Submit</button>
      </div>
    );
  }

  return (
    <div style={{ padding: '20px' }}>
      <h2>Admin Upload Page</h2>

      <label htmlFor="weekSelector">Select Week: </label>
      <select id="weekSelector" value={selectedWeek} onChange={handleWeekChange}>
        {[...Array(16).keys()].reverse().map((week) => (
          <option key={week + 1} value={week + 1}>
            Week {week + 1}
          </option>
        ))}
      </select>

      <div style={{ marginTop: '20px' }}>
        <h3>Upload Game Data</h3>
        <textarea
          rows="8"
          cols="60"
          placeholder="Paste JSON-formatted game data here"
          value={gameData}
          onChange={(e) => setGameData(e.target.value)}
        ></textarea>
        <br />
        <button onClick={handleGameDataUpload}>Upload Games</button>
      </div>

      <div style={{ marginTop: '40px' }}>
        <h3>Upload Winner Data</h3>
        <textarea
          rows="8"
          cols="60"
          placeholder="Paste JSON-formatted winner data here"
          value={winnerData}
          onChange={(e) => setWinnerData(e.target.value)}
        ></textarea>
        <br />
        <button onClick={handleWinnerDataUpload}>Upload Winners</button>
      </div>
    </div>
  );
}

export default AdminUploadPage;
