// src/components/PrintableGamesPage.jsx
import React, { useEffect, useState, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import html2pdf from 'html2pdf.js';
import printIcon from '../assets/print-icon.png';

const PrintableGamesPage = () => {
  const { weekNumber } = useParams();
  const [games, setGames] = useState([]);
  const navigate = useNavigate();
  const contentRef = useRef();

  useEffect(() => {
    fetch(`http://localhost:4000/api/games/${weekNumber}`)
      .then((res) => res.json())
      .then((data) => {
        // Ensure we get an array. If not, default to empty array.
        if (Array.isArray(data)) {
          setGames(data);
        } else {
          console.error('Data is not an array:', data);
          setGames([]);
        }
      })
      .catch((err) => {
        console.error('Error fetching games:', err);
        setGames([]);
      });
  }, [weekNumber]);

  const handleWeekChange = (e) => {
    navigate(`/print/week/${e.target.value}`);
  };

  const handlePrint = () => {
    window.print();
  };

  const handleExportPDF = () => {
    if (!contentRef.current) return;
    html2pdf().from(contentRef.current).save(`Week_${weekNumber}_Games.pdf`);
  };

  return (
    <div>
      <h2>Printable Game List - Week</h2>
      <div>
        <label>Select Week: </label>
        <select value={weekNumber} onChange={handleWeekChange}>
          {Array.from({ length: 18 }, (_, i) => (
            <option key={i + 1} value={i + 1}>
              Week {i + 1}
            </option>
          ))}
        </select>
        <button onClick={handleExportPDF}>
          <img src={printIcon} alt="Print Icon" style={{ width: 16, marginRight: 5 }} />
          Export to PDF
        </button>
        <button onClick={handlePrint}>Print This Page</button>
      </div>
      <div ref={contentRef} style={{ marginTop: '20px' }}>
        {games.length === 0 ? (
          <p>No games found for Week {weekNumber}.</p>
        ) : (
          <ul>
            {games.map((game, index) => (
              <li key={index}>
                {game.team1} vs {game.team2} ({game.spread})
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default PrintableGamesPage;
