import React, { useEffect, useState } from 'react';

const AdminPicksPage = () => {
  const [picks, setPicks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('http://localhost:4000/api/picks')
      .then((res) => res.json())
      .then((data) => {
        setPicks(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error('Error loading picks:', err);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <div style={{ padding: '1rem' }}>Loading submitted picks...</div>;
  }

  if (picks.length === 0) {
    return <div style={{ padding: '1rem' }}>No picks submitted yet.</div>;
  }

  return (
    <div style={{ padding: '1rem', fontFamily: 'Arial' }}>
      <h1 style={{ fontSize: '24px', fontWeight: 'bold', marginBottom: '1rem' }}>Admin Picks Viewer</h1>
      {picks.map((entry, index) => (
        <div key={index} style={{ border: '1px solid #ccc', marginBottom: '1rem', padding: '1rem', borderRadius: '8px' }}>
          <h3 style={{ marginBottom: '0.5rem' }}>
            {entry.name} – <span style={{ fontSize: '14px', color: '#555' }}>{new Date(entry.timestamp).toLocaleString()}</span>
          </h3>
          <ul style={{ marginLeft: '1rem' }}>
            {entry.picks.map((team, i) => (
              <li key={i}>• {team}</li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
};

export default AdminPicksPage;
