import React, { useEffect, useState } from "react";

const PlayerPicksPage = () => {
  const [games, setGames] = useState([]);
  const [selectedTeams, setSelectedTeams] = useState([]);
  const [playerName, setPlayerName] = useState("");
  const [warning, setWarning] = useState("");

  useEffect(() => {
    fetch("http://localhost:4000/api/games")
      .then((res) => res.json())
      .then((data) => setGames(data))
      .catch((err) => console.error("Error loading games:", err));
  }, []);

  const toggleTeam = (team) => {
    if (selectedTeams.includes(team)) {
      setSelectedTeams(selectedTeams.filter((t) => t !== team));
    } else if (selectedTeams.length < 10) {
      setSelectedTeams([...selectedTeams, team]);
    } else {
      setWarning("You can only pick 10 teams.");
    }
  };

  const handleSubmit = () => {
    if (selectedTeams.length !== 10) {
      setWarning("You must pick exactly 10 teams.");
      return;
    }
    if (!playerName.trim()) {
      setWarning("Please enter your game name.");
      return;
    }

    // Submit picks to backend
    fetch("http://localhost:4000/api/picks", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        name: playerName.trim(),
        picks: selectedTeams,
        timestamp: new Date().toISOString()
      }),
    })
      .then((res) => res.json())
      .then(() => {
        alert("Picks submitted successfully!");
        setSelectedTeams([]);
        setPlayerName("");
        setWarning("");
      })
      .catch((err) => {
        console.error("Submission error:", err);
        setWarning("Failed to submit picks. Try again.");
      });
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial" }}>
      <h1>Make Your Picks</h1>

      <input
        type="text"
        placeholder="Enter your game name"
        value={playerName}
        onChange={(e) => setPlayerName(e.target.value)}
        style={{ padding: "0.5rem", marginBottom: "1rem", width: "300px" }}
      />

      {games.map((game, index) => (
        <div key={index} style={{ marginBottom: "1rem" }}>
          <div>
            <button
              onClick={() => toggleTeam(game.team1)}
              style={{
                padding: "0.5rem",
                marginRight: "1rem",
                backgroundColor: selectedTeams.includes(game.team1) ? "#ffd700" : "#eee"
              }}
            >
              {game.team1} ({game.spread})
            </button>
            <button
              onClick={() => toggleTeam(game.team2)}
              style={{
                padding: "0.5rem",
                backgroundColor: selectedTeams.includes(game.team2) ? "#ffd700" : "#eee"
              }}
            >
              {game.team2} ({-game.spread})
            </button>
          </div>
        </div>
      ))}

      <div style={{ marginTop: "1rem" }}>
        <button onClick={handleSubmit} style={{ padding: "0.75rem 2rem" }}>
          Submit Picks
        </button>
      </div>

      {warning && (
        <div style={{ marginTop: "1rem", color: "red" }}>
          <strong>Warning:</strong> {warning}
        </div>
      )}
    </div>
  );
};

export default PlayerPicksPage;
