import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PlayerPicksPage = () => {
  const [games, setGames] = useState([]);
  const [playerName, setPlayerName] = useState('');
  const [picks, setPicks] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:4000/api/games/1')
      .then(res => setGames(res.data))
      .catch(err => console.error('Failed to load games:', err));
  }, []);

  const togglePick = (team) => {
    if (picks.includes(team)) {
      setPicks(prev => prev.filter(p => p !== team));
    } else {
      if (picks.length >= 10) {
        alert("You can only select up to 10 teams.");
        return;
      }
      setPicks(prev => [...prev, team]);
    }
  };

  const handleSubmit = () => {
    if (!playerName) {
      alert("Please enter your game name.");
      return;
    }

    if (picks.length !== 10) {
      alert("You must select exactly 10 teams.");
      return;
    }

    const confirm = window.confirm("Are you sure? Once you submit your picks, they cannot be changed.");
    if (!confirm) return;

    const data = { name: playerName, picks };

    axios.post('http://localhost:4000/api/picks', data)
      .then(() => alert('Picks submitted successfully!'))
      .catch(err => alert('Error submitting picks.'));
  };

  return (
    <div className="p-4 max-w-2xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Player Picks - Week 1</h1>

      <input
        className="border p-2 w-full mb-4"
        placeholder="Enter your game name"
        value={playerName}
        onChange={(e) => setPlayerName(e.target.value)}
      />

      <p className="mb-2 text-sm text-gray-600">Selected: {picks.length} of 10 teams</p>

      <div className="space-y-4">
        {games.map((game, index) => (
          <div key={index} className="border p-3 rounded">
            <p>{game.date} â€” {game.time}</p>
            <p>
              <button
                className={`mr-2 p-2 rounded border ${picks.includes(game.team1) ? 'bg-green-200' : ''}`}
                onClick={() => togglePick(game.team1)}
              >
                {game.team1}
              </button>
              vs
              <button
                className={`ml-2 p-2 rounded border ${picks.includes(game.team2) ? 'bg-green-200' : ''}`}
                onClick={() => togglePick(game.team2)}
              >
                {game.team2}
              </button>
            </p>
            <p className="text-sm text-gray-600">Spread: {game.spread}</p>
          </div>
        ))}
      </div>

      <button
        className="mt-6 bg-blue-500 text-white px-4 py-2 rounded"
        onClick={handleSubmit}
      >
        Submit Picks
      </button>
    </div>
  );
};

export default PlayerPicksPage;
