import React, { useEffect, useState } from 'react';
import { getCurrentWeek } from '../utils/getCurrentWeek';

const AllPlayersPicksPage = () => {
  const [currentWeek, setCurrentWeek] = useState(1);
  const [playerData, setPlayerData] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    getCurrentWeek().then(week => {
      setCurrentWeek(week);
      fetch(`/api/player-picks/${week}`)
        .then(res => res.json())
        .then(data => {
          setPlayerData(data);
          setLoading(false);
        })
        .catch(err => {
          console.error('Error loading player picks:', err);
          setPlayerData({});
          setLoading(false);
        });
    });
  }, []);

  if (loading) return <div style={{ padding: '20px' }}>Loading All Player Picks...</div>;

  return (
    <div style={{ padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
      <h2 style={{ fontSize: '20px', fontWeight: 'bold', marginBottom: '16px' }}>
        All Players' Picks - Week {currentWeek}
      </h2>

      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr style={{ backgroundColor: '#f0f0f0' }}>
            <th style={thStyle}>Game Name</th>
            <th style={thStyle}>Picks</th>
            <th style={thStyle}>Winning Picks</th>
            <th style={thStyle}>Weekly Points</th>
            <th style={thStyle}>Cumulative</th>
          </tr>
        </thead>
        <tbody>
          {Object.entries(playerData).map(([player, data], idx) => (
            <tr key={idx}>
              <td style={tdStyle}>{player}</td>
              <td style={tdStyle}>
                {data?.picks?.length
                  ? data.picks.map(p => p.team).join(', ')
                  : 'No picks submitted'}
              </td>
              <td style={tdStyle}>
                {data?.picks?.length && data.picks[0].hasOwnProperty('isCorrect')
                  ? data.picks.filter(p => p.isCorrect).map(p => p.team).join(', ') || 'None'
                  : 'No data available until Tuesday'}
              </td>
              <td style={tdStyle}>
                {data?.picks?.length && data.picks[0].hasOwnProperty('isCorrect')
                  ? data.weekly
                  : 'N/A'}
              </td>
              <td style={tdStyle}>{data.total ?? 'N/A'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const thStyle = {
  padding: '10px',
  borderBottom: '2px solid #ccc',
  textAlign: 'left'
};

const tdStyle = {
  padding: '8px',
  borderBottom: '1px solid #ddd',
  verticalAlign: 'top'
};

export default AllPlayersPicksPage;
