// frontend/src/App.js

import React from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";

import IntroPage from "./components/IntroPage";
import PlaceYourPicksPage from "./components/PlaceYourPicksPage";
import PlayerPicksPage from "./components/PlayerPicksPage";
import PlayerRosterPaymentsPage from "./components/PlayerRosterPaymentsPage";
import PlayersPage from "./components/PlayersPage";
import PrintableGamesPage from "./components/PrintableGamesPage";
import ResultsPage from "./components/ResultsPage";
import RulesPage from "./components/RulesPage";
import ScoredPicksPage from "./components/ScoredPicksPage";
import SpreadByWeekPage from "./components/SpreadByWeekPage";
import AdminUploadTool from "./components/AdminUploadTool";
import AdminApproveGamesPage from "./components/AdminApproveGamesPage";
import AdminUploadScores from "./components/AdminUploadScores";
import ViewPicksPage from "./components/ViewPicksPage";
import ViewScoresPage from "./components/ViewScoresPage"; // ✅ Your active Scores page
import WeeklyScoresPage from "./components/WeeklyScoresPage";
import WeekSelector from "./components/WeekSelector";

function App() {
  return (
    <Router>
      <div className="App">
        <nav style={{ marginBottom: "20px" }}>
          <ul style={{ listStyle: "none", padding: 0, display: "flex", gap: "15px", flexWrap: "wrap" }}>
            <li><Link to="/">Player Picks</Link></li>
            <li><Link to="/print/week/1">Printable Page</Link></li>
            <li><Link to="/admin/upload">Upload Spreads</Link></li>
            <li><Link to="/admin/approve">Approve Games</Link></li>
            <li><Link to="/admin/upload-scores">Upload Scores</Link></li>
            <li><Link to="/results/week/1">Results Page</Link></li>
            <li><Link to="/scores/week/1">Score Page</Link></li>
          </ul>
        </nav>

        <Routes>
          <Route path="/" element={<PlayerPicksPage />} />
          <Route path="/print/week/:weekNumber" element={<PrintableGamesPage />} />
          <Route path="/admin/upload" element={<AdminUploadTool />} />
          <Route path="/admin/approve" element={<AdminApproveGamesPage />} />
          <Route path="/admin/upload-scores" element={<AdminUploadScores />} />
          <Route path="/results/week/:weekNumber" element={<ResultsPage />} />
          <Route path="/scores/week/:weekNumber" element={<ViewScoresPage />} /> {/* ✅ Your score page */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
