// src/components/ViewScoresPage.jsx
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const ViewScoresPage = () => {
  const { week } = useParams();
  const [scores, setScores] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:4000/api/scores/${week}`)
      .then(res => res.json())
      .then(data => setScores(data))
      .catch(err => console.error('Error loading scores:', err));
  }, [week]);

  return (
    <div className="max-w-2xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Weekly Results - Week {week}</h1>
      <ul className="space-y-2">
        {scores.map((entry, idx) => {
          const { player, correct, total } = entry;
          const isGoldStar = correct >= 9;

          return (
            <li
              key={idx}
              className={`p-3 border rounded shadow ${
                isGoldStar ? 'bg-yellow-50 border-yellow-400' : 'bg-white border-gray-300'
              }`}
            >
              <span className="font-semibold">{player}</span>: {correct}/{total} correct
              {isGoldStar && (
                <span className="ml-3 text-yellow-600 font-bold">
                  ðŸŒŸ Amazing picks!
                </span>
              )}
            </li>
          );
        })}
      </ul>
    </div>
  );
};

export default ViewScoresPage;
