import React, { useState, useEffect } from 'react';
import axios from 'axios';

function AdminUploadPage() {
  const [week, setWeek] = useState(1);
  const [gameData, setGameData] = useState('');
  const [winnerData, setWinnerData] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [passwordInput, setPasswordInput] = useState('');

  const correctPassword = 'football2025'; // Change this to your preferred password

  useEffect(() => {
    setWeek(1);
  }, []);

  const handlePasswordSubmit = () => {
    if (passwordInput === correctPassword) {
      setIsAuthenticated(true);
    } else {
      alert('Incorrect password.');
    }
  };

  const handleUploadGames = async () => {
    try {
      const parsedData = JSON.parse(gameData);
      await axios.post(`http://localhost:4000/api/games/${week}`, parsedData);
      alert('Game data uploaded successfully!');
    } catch (error) {
      alert('Failed to upload game data. Make sure it is valid JSON.');
    }
  };

  const handleUploadWinners = async () => {
    try {
      const parsedData = JSON.parse(winnerData);
      await axios.post(`http://localhost:4000/api/winners/${week}`, parsedData);
      alert('Winner data uploaded successfully!');
    } catch (error) {
      alert('Failed to upload winner data. Make sure it is valid JSON.');
    }
  };

  if (!isAuthenticated) {
    return (
      <div style={{ padding: '20px' }}>
        <h2>Admin Access</h2>
        <input
          type="password"
          placeholder="Enter admin password"
          value={passwordInput}
          onChange={(e) => setPasswordInput(e.target.value)}
        />
        <button onClick={handlePasswordSubmit}>Submit</button>
      </div>
    );
  }

  return (
    <div style={{ padding: '20px' }}>
      <h2>Admin Upload Page</h2>

      <label>Select Week: </label>
      <select value={week} onChange={(e) => setWeek(e.target.value)}>
        {[...Array(16).keys()].map((i) => (
          <option key={i + 1} value={16 - i}>
            Week {16 - i}
          </option>
        ))}
      </select>

      <h3>Upload Game Data</h3>
      <textarea
        rows="7"
        cols="60"
        placeholder="Paste JSON-formatted game data here"
        value={gameData}
        onChange={(e) => setGameData(e.target.value)}
      />
      <br />
      <button onClick={handleUploadGames}>Upload Games</button>

      <h3>Upload Winner Data</h3>
      <textarea
        rows="5"
        cols="60"
        placeholder="Paste JSON-formatted winner data here"
        value={winnerData}
        onChange={(e) => setWinnerData(e.target.value)}
      />
      <br />
      <button onClick={handleUploadWinners}>Upload Winners</button>
    </div>
  );
}

export default AdminUploadPage;
