import React, { useEffect, useState } from 'react';
import { getCurrentWeek } from '../utils/getCurrentWeek';

const WeeklyScoresPage = () => {
  const [scores, setScores] = useState([]);
  const [currentWeek, setCurrentWeek] = useState(1);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    getCurrentWeek().then(week => {
      setCurrentWeek(week);
      fetch(`/data/scores_week_${week}.json`)
        .then(res => res.json())
        .then(data => {
          setScores(data);
          setLoading(false);
        })
        .catch(err => {
          console.error('Error loading scores:', err);
          setScores([]);
          setLoading(false);
        });
    });
  }, []);

  if (loading) return <div style={{ padding: '20px' }}>Loading scores...</div>;

  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: '0 auto' }}>
      <h2 style={{ fontSize: '20px', fontWeight: 'bold', marginBottom: '16px' }}>
        Weekly Scores - Week {currentWeek}
      </h2>

      {scores.length > 0 ? (
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {scores.map((game, idx) => (
            <li key={idx} style={{ marginBottom: '10px' }}>
              {game.team1} ({game.team1_score}) vs {game.team2} ({game.team2_score})
            </li>
          ))}
        </ul>
      ) : (
        <p>No scores available yet.</p>
      )}
    </div>
  );
};

export default WeeklyScoresPage;
