import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PlayerPicksPage = () => {
  const [games, setGames] = useState([]);
  const [playerName, setPlayerName] = useState('');
  const [picks, setPicks] = useState([]);
  const [selectedWeek, setSelectedWeek] = useState(1);

  useEffect(() => {
    axios.get(`http://localhost:4000/api/games/${selectedWeek}`)
      .then(res => setGames(res.data))
      .catch(err => console.error('Failed to load games:', err));
  }, [selectedWeek]);

  const togglePick = (team) => {
    if (picks.includes(team)) {
      setPicks(prev => prev.filter(p => p !== team));
    } else {
      if (picks.length >= 10) {
        alert("You can only select up to 10 teams.");
        return;
      }
      setPicks(prev => [...prev, team]);
    }
  };

  function handleSubmit() {
    if (!playerName) {
      alert("Please enter your game name.");
      return;
    }

    if (picks.length !== 10) {
      alert("You must select exactly 10 teams.");
      return;
    }

    const confirm = window.confirm("Are you sure? Once you submit your picks, they cannot be changed.");
    if (!confirm) return;

    const data = { name: playerName, picks, week: selectedWeek };

    axios.post('http://localhost:4000/api/picks', data)
      .then(() => {
        const pickList = picks.map(p => `‚Ä¢ ${p}`).join('\n');
        alert(`Thanks, ${playerName}! Your picks for Week ${selectedWeek} have been submitted:\n\n${pickList}`);
      })
      .catch(err => {
        console.error(err);
        alert('Error submitting picks.');
      });
  }
  return (
    <div className="p-4 max-w-3xl mx-auto">
      <h1 className="text-3xl font-bold mb-4 text-center">üèà Player Picks - Week {selectedWeek}</h1>

      <div className="mb-4 text-center">
        <label className="mr-2 font-semibold">Select Week:</label>
        <select
          className="border px-3 py-1 rounded"
          value={selectedWeek}
          onChange={(e) => {
            setSelectedWeek(parseInt(e.target.value));
            setPicks([]); // clear picks when switching weeks
          }}
        >
          {[...Array(18).keys()].slice(1).map(week => (
            <option key={week} value={week}>Week {week}</option>
          ))}
        </select>
      </div>

      <input
        className="border p-2 w-full mb-2 rounded"
        placeholder="Enter your game name"
        value={playerName}
        onChange={(e) => setPlayerName(e.target.value)}
      />

      <p className="mb-4 text-sm text-gray-600 text-center">Selected: {picks.length} of 10 teams</p>

      <div className="space-y-4">
        {games.map((game, index) => (
          <div key={index} className="border p-4 rounded-lg shadow">
            <p className="text-sm text-gray-500 mb-1">{game.date} ‚Äî {game.time}</p>

            <div className="flex justify-between items-center mb-2">
              <button
                className={`flex-1 mr-2 py-2 px-4 rounded-lg font-semibold border
                  ${picks.includes(game.team1) ? 'bg-green-100' : 'bg-gray-100 hover:bg-gray-200'}`}
                onClick={() => togglePick(game.team1)}
              >
                {game.team1}
                {game.spread.includes('-') ? ` (${game.spread})` : ''}
                {picks.includes(game.team1) && <span className="ml-2 text-green-600 font-bold">Picked</span>}
              </button>

              <span className="text-sm text-gray-700 font-bold">vs</span>

              <button
                className={`flex-1 ml-2 py-2 px-4 rounded-lg font-semibold border
                  ${picks.includes(game.team2) ? 'bg-green-100' : 'bg-gray-100 hover:bg-gray-200'}`}
                onClick={() => togglePick(game.team2)}
              >
                {game.team2}
                {game.spread.includes('+') ? ` (${game.spread})` : (game.spread === 'even' ? ' (even)' : '')}
                {picks.includes(game.team2) && <span className="ml-2 text-green-600 font-bold">Picked</span>}
              </button>
            </div>
          </div>
        ))}
      </div>

      <button
        className="mt-6 bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded-lg w-full text-lg font-bold"
        onClick={handleSubmit}
      >
        Submit Picks
      </button>
    </div>
  );
};

export default PlayerPicksPage;
