import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const WeekSelector = ({ currentWeek }) => {
  const navigate = useNavigate();
  const location = useLocation();

  const handleChange = (e) => {
    const newWeek = e.target.value;
    if (location.pathname.includes('/picks')) {
      navigate(`/picks/week/${newWeek}`);
    } else if (location.pathname.includes('/print')) {
      navigate(`/print/week/${newWeek}`);
    } else if (location.pathname.includes('/view-scores')) {
      navigate(`/view-scores/${newWeek}`);
    }
  };

  return (
    <div className="mb-4">
      <label htmlFor="week" className="mr-2 font-semibold">Select Week:</label>
      <select
        id="week"
        value={currentWeek}
        onChange={handleChange}
        className="p-2 border rounded"
      >
        {[1, 2, 3, 4, 5].map(w => (
          <option key={w} value={w}>
            Week {w}
          </option>
        ))}
      </select>
    </div>
  );
};

export default WeekSelector;
