import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:4000');

const ChatPage = () => {
  const [user, setUser] = useState('');
  const [text, setText] = useState('');
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    socket.on('chatHistory', history => {
      setMessages(history);
    });

    socket.on('chatMessage', msg => {
      setMessages(prev => [...prev, msg]);
    });

    return () => {
      socket.off('chatHistory');
      socket.off('chatMessage');
    };
  }, []);

  const sendMessage = () => {
    if (text.trim() !== '') {
      socket.emit('chatMessage', { user, text });
      setText('');
    }
  };

  return (
    <div style={{ padding: '20px', maxWidth: '700px', margin: '0 auto' }}>
      <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>üèà Team Chat Zone</h2>

      <div style={{ marginBottom: '12px' }}>
        <input
          type="text"
          placeholder="Enter your game name"
          value={user}
          onChange={e => setUser(e.target.value)}
          style={{ width: '100%', padding: '8px', marginBottom: '10px' }}
        />
        <div style={{ maxHeight: '300px', overflowY: 'scroll', border: '1px solid #ccc', padding: '10px', borderRadius: '8px', background: '#f9f9f9' }}>
          {messages.map((msg, idx) => (
            <div key={idx} style={{ marginBottom: '8px' }}>
              <strong>{msg.user || 'Anon'}:</strong> {msg.text}
              <div style={{ fontSize: '11px', color: '#888' }}>{new Date(msg.timestamp).toLocaleTimeString()}</div>
            </div>
          ))}
        </div>
      </div>

      <div style={{ display: 'flex', gap: '10px' }}>
        <input
          type="text"
          placeholder="Type your message"
          value={text}
          onChange={e => setText(e.target.value)}
          onKeyDown={e => e.key === 'Enter' && sendMessage()}
          style={{ flex: 1, padding: '8px' }}
        />
        <button onClick={sendMessage} style={{ padding: '8px 12px' }}>Send</button>
      </div>
    </div>
  );
};

export default ChatPage;
