import React, { useState } from "react";

const sampleGames = [
  { id: 1, team1: "Cowboys", team2: "Eagles" },
  { id: 2, team1: "Chiefs", team2: "Bills" },
  { id: 3, team1: "Packers", team2: "Bears" },
  { id: 4, team1: "Rams", team2: "49ers" },
];

function App() {
  const [selectedTeams, setSelectedTeams] = useState([]);
  const [playerName, setPlayerName] = useState("");

  const handleCheckboxChange = (team) => {
    if (selectedTeams.includes(team)) {
      setSelectedTeams(selectedTeams.filter((t) => t !== team));
    } else if (selectedTeams.length < 10) {
      setSelectedTeams([...selectedTeams, team]);
    } else {
      alert("You can only select up to 10 teams.");
    }
  };

  const handleSubmit = async () => {
    if (!playerName.trim()) {
      alert("Please enter your name.");
      return;
    }

    if (selectedTeams.length === 0) {
      alert("Please select at least one team.");
      return;
    }

    const pickData = {
      name: playerName.trim(),
      picks: selectedTeams,
    };

    try {
      const response = await fetch("http://localhost:4000/api/picks", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(pickData),
      });

      if (response.ok) {
        alert("‚úÖ Your picks were submitted successfully!");
        setSelectedTeams([]);
        setPlayerName("");
      } else {
        alert("‚ùå There was a problem saving your picks.");
      }
    } catch (error) {
      alert("üö® Could not connect to the server.");
    }
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial" }}>
      <h1>üèà Make Your Picks</h1>

      <div style={{ marginBottom: "1rem" }}>
        <label>
          <strong>Your Name: </strong>
          <input
            type="text"
            value={playerName}
            onChange={(e) => setPlayerName(e.target.value)}
            style={{ marginLeft: "0.5rem", padding: "0.25rem", width: "200px" }}
          />
        </label>
      </div>

      {sampleGames.map((game) => (
        <div key={game.id} style={{ marginBottom: "1rem" }}>
          <strong>{game.team1} vs {game.team2}</strong>
          <div style={{ marginTop: "0.5rem" }}>
            <label>
              <input
                type="checkbox"
                checked={selectedTeams.includes(game.team1)}
                onChange={() => handleCheckboxChange(game.team1)}
              />
              {game.team1}
            </label>
            <label style={{ marginLeft: "1rem" }}>
              <input
                type="checkbox"
                checked={selectedTeams.includes(game.team2)}
                onChange={() => handleCheckboxChange(game.team2)}
              />
              {game.team2}
            </label>
          </div>
        </div>
      ))}

      <p style={{ marginTop: "1rem" }}>
        ‚úÖ <strong>{selectedTeams.length}</strong> team(s) selected out of 10
      </p>

      <button
        onClick={handleSubmit}
        disabled={selectedTeams.length === 0 || !playerName.trim()}
        style={{
          padding: "0.5rem 1rem",
          fontSize: "1rem",
          backgroundColor:
            selectedTeams.length === 0 || !playerName.trim()
              ? "#ccc"
              : "#28a745",
          color: "white",
          border: "none",
          borderRadius: "4px",
          cursor:
            selectedTeams.length === 0 || !playerName.trim()
              ? "not-allowed"
              : "pointer",
        }}
      >
        Submit Picks
      </button>
    </div>
  );
}

export default App;
