import React, { useEffect, useState } from 'react';
import { getCurrentWeek } from '../utils/getCurrentWeek';

const SpreadPage = () => {
  const [games, setGames] = useState([]);
  const [currentWeek, setCurrentWeek] = useState(1);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    getCurrentWeek().then(week => {
      setCurrentWeek(week);
      fetch(`/data/games_week_${week}.json`)
        .then(res => res.json())
        .then(data => {
          setGames(data);
          setLoading(false);
        })
        .catch(err => {
          console.error('Error loading games:', err);
          setGames([]);
          setLoading(false);
        });
    });
  }, []);

  if (loading) return <div style={{ padding: '20px' }}>Loading game spreads...</div>;

  return (
    <div style={{ padding: '20px', maxWidth: '900px', margin: '0 auto' }}>
      <h2 style={{ fontSize: '20px', fontWeight: 'bold', marginBottom: '16px' }}>
        Game Spread - Week {currentWeek}
      </h2>

      {games.length > 0 ? (
        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr style={{ backgroundColor: '#f0f0f0' }}>
              <th style={thStyle}>Date</th>
              <th style={thStyle}>Time</th>
              <th style={thStyle}>Team 1</th>
              <th style={thStyle}>Spread</th>
              <th style={thStyle}>Team 2</th>
              <th style={thStyle}>Spread</th>
            </tr>
          </thead>
          <tbody>
            {games.map((game, idx) => (
              <tr key={idx}>
                <td style={tdStyle}>{game.date}</td>
                <td style={tdStyle}>{game.time}</td>
                <td style={tdStyle}>{game.team1}</td>
                <td style={tdStyle}>{game.team1_spread}</td>
                <td style={tdStyle}>{game.team2}</td>
                <td style={tdStyle}>{game.team2_spread}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No spread available yet.</p>
      )}
    </div>
  );
};

const thStyle = {
  padding: '10px',
  borderBottom: '2px solid #ccc',
  textAlign: 'left'
};

const tdStyle = {
  padding: '8px',
  borderBottom: '1px solid #ddd'
};

export default SpreadPage;
