// React component: Intro Page with Image Background and Login Overlay
import React, { useState } from 'react';
import PlayersPage from './PlayersPage'; // assumes PlayersPage is in the same components folder
import image from '../assets/shama-lama-cover.png'; // path to your uploaded image

const IntroPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [accessGranted, setAccessGranted] = useState(false);
  const [error, setError] = useState('');

  const correctUsername = 'ShamaLama2025';
  const correctPassword = 'ShamaLama2025';

  const handleLogin = () => {
    if (username === correctUsername && password === correctPassword) {
      setAccessGranted(true);
    } else {
      setError('‚ùå Incorrect Username or Password. Please try again.');
    }
  };

  if (accessGranted) return <PlayersPage />;

  return (
    <div
      className="relative min-h-screen bg-cover bg-center flex items-center justify-center"
      style={{ backgroundImage: `url(${image})` }}
    >
      <div className="bg-white bg-opacity-90 p-6 rounded shadow-lg w-80">
        <h1 className="text-xl font-bold mb-4 text-center">üèà Welcome to Shama Lama Ding Dong Pick'em</h1>
        <label className="block mb-2 font-semibold">Username:</label>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          className="border p-2 w-full mb-3"
        />
        <label className="block mb-2 font-semibold">Password:</label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="border p-2 w-full mb-3"
        />
        <button
          onClick={handleLogin}
          className="bg-blue-600 text-white w-full py-2 rounded hover:bg-blue-700"
        >
          Enter
        </button>
        {error && <p className="text-red-600 mt-3 text-center">{error}</p>}
      </div>
    </div>
  );
};

export default IntroPage;
