import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ViewScoresPage = () => {
  const [selectedWeek, setSelectedWeek] = useState(1);
  const [scores, setScores] = useState([]);

  useEffect(() => {
    axios.get(`http://localhost:4000/api/scores/${selectedWeek}`)
      .then(res => setScores(res.data))
      .catch(err => console.error('Failed to load scores:', err));
  }, [selectedWeek]);

  return (
    <div className="p-4 max-w-2xl mx-auto">
      <h1 className="text-3xl font-bold mb-4 text-center">ðŸ“Š Weekly Scores</h1>

      <div className="mb-4 text-center">
        <label className="mr-2 font-semibold">Select Week:</label>
        <select
          className="border px-3 py-1 rounded"
          value={selectedWeek}
          onChange={(e) => setSelectedWeek(parseInt(e.target.value))}
        >
          {[...Array(18).keys()].slice(1).map(week => (
            <option key={week} value={week}>Week {week}</option>
          ))}
        </select>
      </div>

      {scores.length === 0 ? (
        <p className="text-center text-gray-600">No scores available for Week {selectedWeek}.</p>
      ) : (
        <table className="w-full border rounded shadow text-center">
          <thead className="bg-gray-100">
            <tr>
              <th className="p-2 border-b">Player</th>
              <th className="p-2 border-b">Correct</th>
              <th className="p-2 border-b">Total</th>
            </tr>
          </thead>
          <tbody>
            {scores.map((score, index) => (
              <tr key={index} className="hover:bg-gray-50">
                <td className="p-2 border-b font-semibold">{score.name}</td>
                <td className="p-2 border-b text-green-600">{score.correct}</td>
                <td className="p-2 border-b">{score.total}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default ViewScoresPage;
