import React, { useEffect, useState } from 'react';

const PlayerPicksPage = () => {
  const [games, setGames] = useState([]);
  const [selectedPicks, setSelectedPicks] = useState([]);
  const [playerName, setPlayerName] = useState('');
  const [message, setMessage] = useState('');
  const [currentWeek, setCurrentWeek] = useState(1);

  useEffect(() => {
    // Load current week (if using current_week.json later)
    fetch('/data/games_week_1.json')
      .then(res => res.json())
      .then(data => setGames(data))
      .catch(err => console.error('Error loading games:', err));
  }, []);

  const handlePick = (team) => {
    if (selectedPicks.includes(team)) {
      setSelectedPicks(selectedPicks.filter(t => t !== team));
    } else if (selectedPicks.length < 10) {
      setSelectedPicks([...selectedPicks, team]);
    }
  };

  const handleSubmit = () => {
    if (!playerName.trim()) {
      setMessage('Please enter your game name.');
      return;
    }
    if (selectedPicks.length !== 10) {
      setMessage('You must select exactly 10 teams.');
      return;
    }

    fetch(`/submit-picks/${currentWeek}`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        player: playerName.trim(),
        picks: selectedPicks
      })
    })
      .then(res => res.json())
      .then(data => setMessage(data.message || 'Picks submitted!'))
      .catch(err => {
        console.error('Submit error:', err);
        setMessage('There was an error submitting your picks.');
      });
  };

  return (
    <div className="p-4 max-w-3xl mx-auto">
      <h2 className="text-xl font-bold mb-4">Place Your Picks - Week {currentWeek}</h2>

      <div className="mb-4">
        <label className="font-semibold mr-2">Game Name:</label>
        <input
          type="text"
          value={playerName}
          onChange={e => setPlayerName(e.target.value)}
          className="border px-2 py-1 rounded"
        />
      </div>

      {games.map((game, idx) => (
        <div key={idx} className="mb-3 border p-3 rounded shadow">
          <div className="text-sm text-gray-600">{game.date} â€” {game.time}</div>
          <div className="mt-2 flex gap-4">
            {[game.team1, game.team2].map(team => (
              <label key={team} className="flex items-center gap-2">
                <input
                  type="radio"
                  name={`game-${idx}`}
                  value={team}
                  checked={selectedPicks.includes(team)}
                  onChange={() => handlePick(team)}
                />
                {team}
              </label>
            ))}
          </div>
        </div>
      ))}

      <div className="text-sm font-medium text-blue-700 mt-4">
        You have selected {selectedPicks.length} out of 10 games.
      </div>

      <button
        onClick={handleSubmit}
        className="mt-4 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
      >
        Submit Picks
      </button>

      <div className="mt-3 text-yellow-800 font-medium">
        Please double-check picks before submitting. Submission is final.
      </div>

      <div className="text-sm text-gray-600 mt-2">
        You will be able to see your selections on the <strong>All Players Picks</strong> page.
        Winning picks and scores will be revealed on Tuesday.
      </div>

      {message && <div className="mt-4 text-green-700 font-semibold">{message}</div>}
    </div>
  );
};

export default PlayerPicksPage;
