// src/components/PrintableGamesPage.jsx

import React, { useEffect, useState, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import html2pdf from 'html2pdf.js';
import printIcon from '../assets/print-icon.png';

const PrintableGamesPage = () => {
  const { weekNumber } = useParams();
  const [games, setGames] = useState([]);
  const navigate = useNavigate();
  const contentRef = useRef();

  useEffect(() => {
    fetch(`http://localhost:4000/api/games/${weekNumber}`)
      .then((res) => res.json())
      .then((data) => setGames(data))
      .catch((err) => console.error('Error fetching games:', err));
  }, [weekNumber]);

  const handleWeekChange = (e) => {
    navigate(`/print/week/${e.target.value}`);
  };

  const handlePrint = () => {
    const element = contentRef.current;
    const opt = {
      margin:       0.5,
      filename:     `Week_${weekNumber}_Games.pdf`,
      image:        { type: 'jpeg', quality: 0.98 },
      html2canvas:  { scale: 2 },
      jsPDF:        { unit: 'in', format: 'letter', orientation: 'portrait' }
    };
    html2pdf().set(opt).from(element).save();
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Printable Game List - Week</h2>
      
      <div style={{ marginBottom: '10px' }}>
        <label>Select Week: </label>
        <select value={weekNumber} onChange={handleWeekChange}>
          {Array.from({ length: 18 }, (_, i) => (
            <option key={i + 1} value={i + 1}>Week {i + 1}</option>
          ))}
        </select>

        <button
          onClick={handlePrint}
          style={{ marginLeft: '10px', padding: '6px 10px', cursor: 'pointer' }}
        >
          <img src={printIcon} alt="Print" style={{ height: '16px', marginRight: '6px' }} />
          Export to PDF
        </button>

        <button
          onClick={() => window.print()}
          style={{ marginLeft: '10px', padding: '6px 10px', cursor: 'pointer' }}
        >
          Print This Page
        </button>
      </div>

      <div ref={contentRef}>
        {games.length === 0 ? (
          <p>No games found for Week {weekNumber}.</p>
        ) : (
          <ul>
            {games.map((game, index) => (
              <li key={index}>
                {game.awayTeam} @ {game.homeTeam} ({game.spread})
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default PrintableGamesPage;
