import React, { useState } from "react";

const games = [
  { id: 1, teamA: "Cowboys", teamB: "Eagles" },
  { id: 2, teamA: "Packers", teamB: "Bears" },
  { id: 3, teamA: "Chiefs", teamB: "Broncos" }
];

function App() {
  const [picks, setPicks] = useState({});

  const handlePick = (gameId, team) => {
    setPicks((prevPicks) => ({
      ...prevPicks,
      [gameId]: team
    }));
  };

  const handleSubmit = () => {
    console.log("Submitted picks:", picks);
    alert("Picks submitted! (Check console)");
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial, sans-serif" }}>
      <h1>Football Pick'em</h1>
      <h2>Make Your Picks</h2>

      {games.map((game) => (
        <div key={game.id} style={{ marginBottom: "1rem" }}>
          <strong>{game.teamA} vs {game.teamB}</strong>
          <div style={{ marginTop: "0.5rem" }}>
            <button
              onClick={() => handlePick(game.id, game.teamA)}
              style={{
                marginRight: "1rem",
                backgroundColor: picks[game.id] === game.teamA ? "lightgreen" : "white"
              }}
            >
              {game.teamA}
            </button>
            <button
              onClick={() => handlePick(game.id, game.teamB)}
              style={{
                backgroundColor: picks[game.id] === game.teamB ? "lightgreen" : "white"
              }}
            >
              {game.teamB}
            </button>
          </div>
        </div>
      ))}

      <button
        onClick={handleSubmit}
        style={{ marginTop: "2rem", padding: "0.5rem 1rem" }}
      >
        Submit Picks
      </button>
    </div>
  );
}

export default App;
