import React, { useEffect, useState } from 'react';

const AllPlayersPicksPage = () => {
  const [availableWeeks, setAvailableWeeks] = useState([]);
  const [selectedWeek, setSelectedWeek] = useState(null);
  const [playerData, setPlayerData] = useState([]);
  const [scoresData, setScoresData] = useState([]);
  const [totalsData, setTotalsData] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const checkAvailableWeeks = async () => {
      const weeks = [];
      for (let i = 1; i <= 15; i++) {
        try {
          const res = await fetch(`/data/picks_week_${i}.json`);
          if (res.ok) {
            weeks.push(i);
          }
        } catch {}
      }
      setAvailableWeeks(weeks);

      try {
        const res = await fetch('/data/current_week.json');
        const data = await res.json();
        const current = data.currentWeek;
        setSelectedWeek(weeks.includes(current) ? current : weeks[weeks.length - 1]);
      } catch {
        setSelectedWeek(weeks[weeks.length - 1]);
      }
    };

    checkAvailableWeeks();
  }, []);

  useEffect(() => {
    if (selectedWeek === null) return;

    const loadAllData = async () => {
      setLoading(true);
      try {
        const [picksRes, scoresRes, totalsRes] = await Promise.all([
          fetch(`/data/picks_week_${selectedWeek}.json`).then(res => res.json()),
          fetch(`/data/scores_week_${selectedWeek}.json`).then(res => res.json()).catch(() => []),
          fetch('/data/totals.json').then(res => res.json()).catch(() => ({}))
        ]);

        setPlayerData(picksRes);
        setScoresData(scoresRes);
        setTotalsData(totalsRes);
      } catch (err) {
        console.error('Error loading All Player data:', err);
        setPlayerData([]);
        setScoresData([]);
        setTotalsData({});
      } finally {
        setLoading(false);
      }
    };

    loadAllData();
  }, [selectedWeek]);

  const getWinners = () => {
    const winners = {};
    scoresData.forEach(game => {
      if (game.team1_score > game.team2_score) {
        winners[game.team1] = true;
      } else if (game.team2_score > game.team1_score) {
        winners[game.team2] = true;
      }
    });
    return winners;
  };

  const winners = getWinners();

  const calculatePoints = (picks) => {
    if (!Array.isArray(picks)) return { correct: [], total: 0 };
    const correct = picks
      .map(p => (typeof p === 'string' ? p : p?.pick))
      .filter(p => winners[p]);
    return { correct, total: correct.length };
  };

  const getPraise = (total) => {
    if (total === 10) return 'üåü Perfect picks!';
    if (total === 9) return '‚≠ê Great job!';
    return '';
  };

  if (loading) return <div style={{ padding: '20px' }}>Loading All Player Picks...</div>;

  return (
    <div style={{ padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
      <h2 style={{ fontSize: '20px', fontWeight: 'bold', marginBottom: '16px' }}>
        All Players' Picks - Week {selectedWeek}
      </h2>

      <div style={{ marginBottom: '16px' }}>
        <label style={{ marginRight: '8px' }}>Select Week:</label>
        <select
          value={selectedWeek || ''}
          onChange={(e) => setSelectedWeek(Number(e.target.value))}
        >
          {availableWeeks.map((week) => (
            <option key={week} value={week}>
              Week {week}
            </option>
          ))}
        </select>
      </div>

      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr style={{ backgroundColor: '#f0f0f0' }}>
            <th style={thStyle}>Game Name</th>
            <th style={thStyle}>Picks</th>
            <th style={thStyle}>Winning Picks</th>
            <th style={thStyle}>Weekly Points</th>
            <th style={thStyle}>Cumulative</th>
          </tr>
        </thead>
        <tbody>
          {playerData
            .map(entry => {
              const { correct, total } = calculatePoints(entry.picks);
              const cumulative = totalsData[entry.player] ?? total;

              return {
                ...entry,
                correct,
                weeklyPoints: total,
                cumulativePoints: cumulative,
                praise: getPraise(total)
              };
            })
            .sort((a, b) => b.cumulativePoints - a.cumulativePoints)
            .map((entry, idx) => (
              <tr key={idx}>
                <td style={tdStyle}>
                  {entry.player || 'Unknown'}
                  {entry.praise && <span style={{ marginLeft: '8px' }}>{entry.praise}</span>}
                </td>
                <td style={tdStyle}>
                  {Array.isArray(entry.picks)
                    ? entry.picks.map(p => (typeof p === 'string' ? p : p?.pick || '?')).join(', ')
                    : 'No picks submitted'}
                </td>
                <td style={tdStyle}>{entry.correct.join(', ') || 'None'}</td>
                <td style={tdStyle}>{entry.weeklyPoints}</td>
                <td style={tdStyle}>{entry.cumulativePoints}</td>
              </tr>
            ))}
        </tbody>
      </table>
    </div>
  );
};

const thStyle = {
  padding: '10px',
  borderBottom: '2px solid #ccc',
  textAlign: 'left'
};

const tdStyle = {
  padding: '8px',
  borderBottom: '1px solid #ddd',
  verticalAlign: 'top'
};

export default AllPlayersPicksPage;
