import React, { useEffect, useState } from 'react';

const RosterPaymentsPage = () => {
  const [roster, setRoster] = useState([]);

  useEffect(() => {
    fetch('http://localhost:4000/data/roster.json')
      .then(res => res.json())
      .then(data => setRoster(data))
      .catch(() => setRoster([]));
  }, []);

  return (
    <div style={{ padding: '30px', maxWidth: '700px', margin: 'auto', fontFamily: 'Arial, sans-serif' }}>
      <h2>Player Roster & Payments</h2>
      <p>Below is the current roster of players and their payment status.</p>

      {roster.length === 0 ? (
        <p>No roster data found. Please upload a valid roster file.</p>
      ) : (
        <table style={{ width: '100%', borderCollapse: 'collapse', fontSize: '16px' }}>
          <thead>
            <tr style={{ borderBottom: '2px solid #000' }}>
              <th style={{ textAlign: 'left' }}>Game Name</th>
              <th style={{ textAlign: 'left' }}>Balance</th>
            </tr>
          </thead>
          <tbody>
            {roster.map((player, index) => (
              <tr key={index} style={{ borderBottom: '1px solid #ccc' }}>
                <td>{player.GameName}</td>
                <td>{player.Balance}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default RosterPaymentsPage;
