import React, { useState } from 'react';

function AdminUploadPage() {
  const [authorized, setAuthorized] = useState(false);
  const [passwordInput, setPasswordInput] = useState('');
  const correctPassword = 'letmein'; // ðŸ‘ˆ Change this to your own private password

  const handlePasswordSubmit = () => {
    if (passwordInput === correctPassword) {
      setAuthorized(true);
    } else {
      alert('Incorrect password. Try again.');
    }
  };

  if (!authorized) {
    return (
      <div className="p-4">
        <h2 className="text-xl font-bold mb-2">Admin Access</h2>
        <input
          type="password"
          placeholder="Enter admin password"
          className="border p-2 mr-2"
          value={passwordInput}
          onChange={(e) => setPasswordInput(e.target.value)}
        />
        <button
          onClick={handlePasswordSubmit}
          className="bg-blue-600 text-white px-4 py-2 rounded"
        >
          Enter
        </button>
      </div>
    );
  }

  // âœ… Your admin upload UI starts here
  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Admin: Upload Weekly Games</h2>
      {/* Add your upload tools, buttons, and preview table below */}
      <p>Welcome, Admin! Add your upload tools here.</p>
    </div>
  );
}

export default AdminUploadPage;
