import React, { useState } from "react";
import * as XLSX from "xlsx";

const AdminUploadTool = () => {
  const [games, setGames] = useState([]);

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const data = await file.arrayBuffer();
    const workbook = XLSX.read(data);
    const sheet = workbook.Sheets[workbook.SheetNames[0]];
    const raw = XLSX.utils.sheet_to_json(sheet, { header: 1 });

    const parsedGames = [];
    for (let i = 0; i < raw.length - 5; i++) {
      const row = raw[i];
      const nextRow = raw[i + 1];
      const nextNextRow = raw[i + 2];

      // Identify a date row, then read following rows for game data
      if (row[0] && typeof row[0] === "string" && row[0].startsWith("Sat")) {
        try {
          const date = row[0].trim();
          const time = nextRow[0]?.toString().trim() || "";
          const teamA = nextRow[1]?.toString().trim() || "";
          const spreadA = nextRow[2]?.toString().split(" ")[0].replace("½", ".5") || "";

          const teamB = raw[i + 5][1]?.toString().trim() || "";
          const spreadB = raw[i + 5][2]?.toString().split(" ")[0].replace("½", ".5") || "";

          if (teamA && teamB && spreadA && spreadB) {
            parsedGames.push({ date, time, teamA, spreadA, teamB, spreadB });
          }

          i += 5; // Skip ahead to avoid overlapping reads
        } catch (err) {
          continue;
        }
      }
    }

    setGames(parsedGames);
  };

  const handleSave = () => {
    console.log("Saved Games:", games);
    alert("Games saved to backend (console only for now)");
  };

  return (
    <div className="p-4 max-w-5xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Admin Upload: Weekly Game List</h1>
      <input
        type="file"
        accept=".xlsx"
        onChange={handleFileUpload}
        className="mb-4"
      />

      {games.length > 0 && (
        <>
          <table className="table-auto w-full border mb-4">
            <thead>
              <tr>
                <th>Date</th>
                <th>Time</th>
                <th>Team A</th>
                <th>Spread A</th>
                <th>Team B</th>
                <th>Spread B</th>
              </tr>
            </thead>
            <tbody>
              {games.map((game, idx) => (
                <tr key={idx}>
                  <td>{game.date}</td>
                  <td>{game.time}</td>
                  <td>{game.teamA}</td>
                  <td>{game.spreadA}</td>
                  <td>{game.teamB}</td>
                  <td>{game.spreadB}</td>
                </tr>
              ))}
            </tbody>
          </table>
          <button
            onClick={handleSave}
            className="bg-green-600 text-white px-4 py-2 rounded"
          >
            Save Week
          </button>
        </>
      )}
    </div>
  );
};

export default AdminUploadTool;
