import React, { useEffect, useState } from 'react';

const AllPlayersPicksPage = ({ currentWeek }) => {
  const [playerData, setPlayerData] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`/api/player-picks/${currentWeek}`)
      .then(res => res.json())
      .then(data => {
        setPlayerData(data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Error loading player picks:', err);
        setLoading(false);
      });
  }, [currentWeek]);

  if (loading) return <div className="p-4 text-center">Loading All Player Picks...</div>;

  return (
    <div className="p-4 max-w-3xl mx-auto">
      <h2 className="text-xl font-bold mb-4">All Players' Picks - Week {currentWeek}</h2>
      {Object.entries(playerData).map(([player, data]) => (
        <div key={player} className="mb-6 border rounded-lg shadow p-4 bg-white">
          <h3 className="font-semibold text-lg mb-2">{player}</h3>
          <div className="flex flex-wrap gap-2">
            {data.picks.map((pick, idx) => (
              <span
                key={idx}
                className={`px-3 py-1 rounded-full text-sm font-medium ${
                  pick.isCorrect ? 'bg-green-200 text-green-800' : 'bg-red-200 text-red-800'
                }`}
              >
                {pick.team}
              </span>
            ))}
          </div>
          <div className="mt-2 text-sm">
            âœ… Correct Picks: <strong>{data.weekly}</strong> &nbsp;&nbsp;
            ðŸ§® Total Points: <strong>{data.total}</strong>
          </div>
        </div>
      ))}
    </div>
  );
};

export default AllPlayersPicksPage;
