import React, { useEffect, useState, useRef } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:4000');

const ChatPage = () => {
  const [name, setName] = useState('');
  const [text, setText] = useState('');
  const [messages, setMessages] = useState([]);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    socket.on('chat message', (msg) => {
      setMessages((prev) => [...prev, msg]);
    });
    return () => socket.off('chat message');
  }, []);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const sendMessage = () => {
    if (!name || !text) return;
    const now = new Date();
    const time = now.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
    socket.emit('chat message', { name, text, time });
    setText('');
  };

  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: '0 auto' }}>
      <h2 style={{ textAlign: 'center', fontSize: '24px', marginBottom: '20px' }}>üèà Team Chat Zone</h2>

      <div style={{
        border: '1px solid #ccc',
        borderRadius: '8px',
        padding: '12px',
        height: '350px',
        overflowY: 'auto',
        marginBottom: '16px',
        backgroundColor: '#f9f9f9'
      }}>
        {messages.map((msg, idx) => (
          <div key={idx} style={{ marginBottom: '10px' }}>
            <span style={{ fontWeight: 'bold', color: '#003366' }}>üó£Ô∏è {msg.name}</span>
            <span style={{ fontSize: '12px', color: '#888', marginLeft: '8px' }}>{msg.time}</span>
            <div style={{ paddingLeft: '20px' }}>{msg.text}</div>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      <input
        type="text"
        placeholder="Enter your game name"
        value={name}
        onChange={(e) => setName(e.target.value)}
        style={{ width: '100%', marginBottom: '8px', padding: '8px' }}
      />
      <textarea
        placeholder="Type your message"
        value={text}
        onChange={(e) => setText(e.target.value)}
        style={{ width: '100%', height: '60px', marginBottom: '8px', padding: '8px' }}
      />
      <button
        onClick={sendMessage}
        style={{ width: '100%', padding: '10px', backgroundColor: '#004225', color: 'white', border: 'none', borderRadius: '4px' }}
      >
        Send
      </button>
    </div>
  );
};

export default ChatPage;
